  1→import { NextRequest } from "next/server";
  2→import { spawn } from "child_process";
  3→import { mkdir, writeFile } from "fs/promises";
  4→import { join } from "path";
  5→import { agentLog, createLogger } from "@/lib/logger";
  6→import { findClaude } from "@/lib/find-claude";
  7→
  8→const debugLog = createLogger("agent-debug");
  9→
 10→export const runtime = "nodejs";
 11→export const dynamic = "force-dynamic";
 12→export const maxDuration = 300;
 13→
 14→const claudePath = findClaude();
 15→agentLog.info("Claude binary resolved", { path: claudePath });
 16→
 17→/**
 18→ * SSE endpoint that streams Claude CLI messages to the renderer.
 19→ * Spawns `claude --print --output-format stream-json` as a subprocess.
 20→ *
 21→ * Key: stdin must be "ignore" — claude CLI hangs if stdin is piped.
 22→ */
 23→interface ImagePayload {
 24→  id: string;
 25→  filename: string;
 26→  mimeType: string;
 27→  base64: string;
 28→  sizeBytes: number;
 29→}
 30→
 31→const MIME_TO_EXT: Record<string, string> = {
 32→  "image/png": "png",
 33→  "image/jpeg": "jpg",
 34→  "image/gif": "gif",
 35→  "image/webp": "webp",
 36→};
 37→
 38→async function saveImages(
 39→  projectDir: string,
 40→  images: ImagePayload[],
 41→  requestId: string
 42→): Promise<string[]> {
 43→  const uploadsDir = join(projectDir, ".cc4d", "uploads");
 44→  await mkdir(uploadsDir, { recursive: true });
 45→
 46→  const savedPaths: string[] = [];
 47→  for (const img of images) {
 48→    const ext = MIME_TO_EXT[img.mimeType] || "png";
 49→    const filename = `img-${Date.now()}-${Math.random().toString(36).slice(2, 6)}.${ext}`;
 50→    const filePath = join(uploadsDir, filename);
 51→    const buffer = Buffer.from(img.base64, "base64");
 52→    await writeFile(filePath, buffer);
 53→    savedPaths.push(filePath);
 54→    agentLog.info(`[${requestId}] Saved image`, { path: filePath, size: buffer.length });
 55→  }
 56→  return savedPaths;
 57→}
 58→
 59→export async function POST(req: NextRequest) {
 60→  const { prompt, images, locale, projectDir, sessionId } = await req.json();
 61→
 62→  const requestId = `req-${Date.now().toString(36)}`;
 63→  agentLog.info(`[${requestId}] New agent request`, {
 64→    locale,
 65→    sessionId: sessionId || null,
 66→    promptLength: prompt.length,
 67→    imageCount: images?.length || 0,
 68→    cwd: projectDir || process.cwd(),
 69→  });
 70→
 71→  const encoder = new TextEncoder();
 72→
 73→  // Save images to disk and build prompt suffix
 74→  let imageSuffix = "";
 75→  if (images?.length && projectDir) {
 76→    try {
 77→      const savedPaths = await saveImages(projectDir, images as ImagePayload[], requestId);
 78→      const pathList = savedPaths
 79→        .map((p) => `Read the image from: ${p}`)
 80→        .join("\n");
 81→      imageSuffix = `\n\n[The user attached ${savedPaths.length} image(s). Use the Read tool to view them:\n${pathList}]`;
 82→    } catch (err) {
 83→      agentLog.error(`[${requestId}] Failed to save images`, { error: String(err) });
 84→    }
 85→  }
 86→
 87→  const localizedPrompt =
 88→    locale === "he"
 89→      ? `[IMPORTANT: Respond in Hebrew. The user speaks Hebrew.]\n\n${prompt}${imageSuffix}`
 90→      : `${prompt}${imageSuffix}`;
 91→
 92→  const args = [
 93→    "--print",
 94→    "--output-format",
 95→    "stream-json",
 96→    "--dangerously-skip-permissions",
 97→    "--verbose",
 98→  ];
 99→
100→  if (sessionId) {
101→    args.push("--resume", sessionId);
102→  }
103→
104→  args.push(localizedPrompt);
105→
106→  const env = { ...process.env };
107→  delete env.CLAUDECODE;
108→
109→  const stream = new ReadableStream({
110→    start(controller) {
111→      agentLog.info(`[${requestId}] Spawning claude`, {
112→        binary: claudePath,
113→        args: args.slice(0, -1), // exclude prompt
114→      });
115→
116→      const child = spawn(claudePath, args, {
117→        cwd: projectDir || process.cwd(),
118→        env,
119→        stdio: ["ignore", "pipe", "pipe"],
120→      });
121→
122→      const childPid = child.pid;
123→      agentLog.info(`[${requestId}] Claude process started`, { pid: childPid });
124→
125→      let buffer = "";
126→      let messageCount = 0;
127→
128→      child.stdout!.on("data", (data: Buffer) => {
129→        buffer += data.toString();
130→        const lines = buffer.split("\n");
131→        buffer = lines.pop() || "";
132→
133→        for (const line of lines) {
134→          if (!line.trim()) continue;
135→          try {
136→            const parsed = JSON.parse(line);
137→            messageCount++;
138→            debugLog.debug(`[${requestId}] raw`, parsed);
139→
140→            // Log message type and key info (not full payload — too large)
141→            const logEntry: Record<string, unknown> = {
142→              type: parsed.type,
143→              seq: messageCount,
144→            };
145→            if (parsed.type === "assistant" && parsed.message?.content) {
146→              const content = parsed.message.content;
147→              if (Array.isArray(content)) {
148→                logEntry.blocks = content.map((b: Record<string, unknown>) => {
149→                  if (b.type === "text") return { type: "text", length: (b.text as string)?.length };
150→                  if (b.type === "tool_use") return { type: "tool_use", name: b.name };
151→                  return { type: b.type };
152→                });
153→              }
154→            }
155→            if (parsed.type === "result") {
156→              logEntry.subtype = parsed.subtype;
157→              logEntry.duration_ms = parsed.duration_ms;
158→              logEntry.num_turns = parsed.num_turns;
159→              logEntry.cost_usd = parsed.total_cost_usd;
160→              logEntry.session_id = parsed.session_id;
161→            }
162→            agentLog.debug(`[${requestId}] SSE event`, logEntry);
163→
164→            controller.enqueue(encoder.encode(`data: ${line}\n\n`));
165→          } catch {
166→            agentLog.warn(`[${requestId}] Non-JSON stdout line`, {
167→              preview: line.slice(0, 200),
168→            });
169→          }
170→        }
171→      });
172→
173→      child.stderr!.on("data", (data: Buffer) => {
174→        const text = data.toString().trim();
175→        if (!text) return;
176→        agentLog.warn(`[${requestId}] Claude stderr`, { text: text.slice(0, 500) });
177→        if (text.includes("Error") || text.includes("error")) {
178→          controller.enqueue(
179→            encoder.encode(
180→              `data: ${JSON.stringify({ type: "error", content: text })}\n\n`
181→            )
182→          );
183→        }
184→      });
185→
186→      child.on("error", (err) => {
187→        agentLog.error(`[${requestId}] Spawn error`, { error: err.message });
188→        controller.enqueue(
189→          encoder.encode(
190→            `data: ${JSON.stringify({ type: "error", content: err.message })}\n\n`
191→          )
192→        );
193→        controller.enqueue(encoder.encode("data: [DONE]\n\n"));
194→        controller.close();
195→      });
196→
197→      child.on("close", (code, signal) => {
198→        agentLog.info(`[${requestId}] Claude process exited`, {
199→          pid: childPid,
200→          code,
201→          signal,
202→          messageCount,
203→        });
204→        if (buffer.trim()) {
205→          try {
206→            JSON.parse(buffer);
207→            controller.enqueue(encoder.encode(`data: ${buffer}\n\n`));
208→          } catch {
209→            // ignore
210→          }
211→        }
212→        controller.enqueue(encoder.encode("data: [DONE]\n\n"));
213→        controller.close();
214→      });
215→
216→      req.signal.addEventListener("abort", () => {
217→        agentLog.info(`[${requestId}] Client disconnected, killing claude`, {
218→          pid: childPid,
219→        });
220→        child.kill("SIGTERM");
221→      });
222→    },
223→  });
224→
225→  return new Response(stream, {
226→    headers: {
227→      "Content-Type": "text/event-stream",
228→      "Cache-Control": "no-cache, no-transform",
229→      Connection: "keep-alive",
230→      "X-Accel-Buffering": "no",
231→    },
232→  });
233→}
