  1â†’"use client";
  2â†’
  3â†’import { useCallback, useEffect, useRef, useState } from "react";
  4â†’import { useRouter } from "next/navigation";
  5â†’import { useTranslations } from "next-intl";
  6â†’import { motion } from "framer-motion";
  7â†’import { Plus } from "lucide-react";
  8â†’import { useAppStore } from "@/lib/store";
  9â†’import { connectToAgent, type AgentCallbacks } from "@/lib/agent-client";
 10â†’import { getPhaseForStep } from "@/lib/progress-config";
 11â†’import { createMilestoneThrottler } from "@/lib/milestone-extractor";
 12â†’import { useChatPersistence } from "@/lib/use-chat-persistence";
 13â†’import { useDebugStore } from "@/lib/debug-store";
 14â†’import { useDebugShortcut } from "@/lib/use-debug-shortcut";
 15â†’import ChatPanel from "@/components/chat/ChatPanel";
 16â†’import LivePreview from "@/components/preview/LivePreview";
 17â†’import DebugTerminal from "@/components/debug/DebugTerminal";
 18â†’import ProjectDrawer from "@/components/project/ProjectDrawer";
 19â†’import ProjectDrawerToggle from "@/components/project/ProjectDrawerToggle";
 20â†’
 21â†’import StepIndicator from "@/components/progress/StepIndicator";
 22â†’import Logo from "@/components/brand/Logo";
 23â†’import LanguageToggle from "@/components/brand/LanguageToggle";
 24â†’
 25â†’function formatInitialMessage(locale: "he" | "en"): string {
 26â†’  return locale === "he"
 27â†’    ? "×”×™×™! ×× ×™ ××•×›×Ÿ/×” ×œ×”×ª×—×™×œ"
 28â†’    : "Hey! I'm ready to start";
 29â†’}
 30â†’
 31â†’export default function BuildPage() {
 32â†’  const t = useTranslations("Build");
 33â†’  const router = useRouter();
 34â†’  const store = useAppStore();
 35â†’  const abortRef = useRef<{ abort: () => void } | null>(null);
 36â†’  const hasStarted = useRef(false);
 37â†’  const milestoneExtractor = useRef(createMilestoneThrottler(store.locale));
 38â†’  const [showRecoveryBanner, setShowRecoveryBanner] = useState(false);
 39â†’  const [refreshTrigger, setRefreshTrigger] = useState(0);
 40â†’
 41â†’  useDebugShortcut();
 42â†’
 43â†’  // Persistence
 44â†’  const { isLoaded } = useChatPersistence(store.projectDir);
 45â†’
 46â†’  // Recover projectDir from electron-store if missing
 47â†’  useEffect(() => {
 48â†’    if (!store.projectDir) {
 49â†’      window.electronAPI?.getActiveProject?.().then((meta: { path?: string; id?: string; name?: string } | null) => {
 50â†’        if (meta?.path) {
 51â†’          store.setProjectDir(meta.path);
 52â†’          if (meta.id) store.setActiveProjectId(meta.id);
 53â†’          if (meta.name) store.setProjectName(meta.name);
 54â†’        }
 55â†’      }).catch(() => {});
 56â†’    }
 57â†’  }, []); // eslint-disable-line react-hooks/exhaustive-deps
 58â†’
 59â†’  // Load milestones when project dir is available
 60â†’  useEffect(() => {
 61â†’    if (!store.projectDir) return;
 62â†’    window.electronAPI?.loadMilestones?.(store.projectDir).then((milestones: unknown[]) => {
 63â†’      if (milestones?.length) {
 64â†’        store.loadMilestones(milestones as Parameters<typeof store.loadMilestones>[0]);
 65â†’      }
 66â†’    }).catch(() => {});
 67â†’  }, [store.projectDir]); // eslint-disable-line react-hooks/exhaustive-deps
 68â†’
 69â†’  // Start agentic flow once persistence has loaded
 70â†’  useEffect(() => {
 71â†’    if (hasStarted.current) return;
 72â†’    if (!isLoaded) return;
 73â†’    if (!store.projectDir) return;
 74â†’    hasStarted.current = true;
 75â†’
 76â†’    // If we have restored messages, show recovery banner and skip initial prompt
 77â†’    if (store.messages.length > 0) {
 78â†’      setShowRecoveryBanner(true);
 79â†’      return;
 80â†’    }
 81â†’
 82â†’    // If resuming a session, don't re-send the initial prompt
 83â†’    if (store.sessionId) return;
 84â†’
 85â†’    sendToAgent(formatInitialMessage(store.locale));
 86â†’  }, [isLoaded, store.projectDir]); // eslint-disable-line react-hooks/exhaustive-deps
 87â†’
 88â†’  const sendToAgent = useCallback(
 89â†’    (prompt: string) => {
 90â†’      // Add user message to chat
 91â†’      store.addMessage({
 92â†’        id: `user-${Date.now()}`,
 93â†’        role: "user",
 94â†’        content: prompt,
 95â†’        timestamp: Date.now(),
 96â†’      });
 97â†’
 98â†’      store.setStreaming(true);
 99â†’
100â†’      abortRef.current = connectToAgent({
101â†’        prompt,
102â†’        locale: store.locale,
103â†’        projectDir: store.projectDir || undefined,
104â†’        sessionId: store.sessionId || undefined,
105â†’        onMessage: (msg) => {
106â†’          store.addMessage(msg);
107â†’          // Extract milestones for the project activity log
108â†’          const milestone = milestoneExtractor.current(msg);
109â†’          if (milestone) {
110â†’            store.addMilestone(milestone);
111â†’            // Persist milestones
112â†’            if (store.projectDir) {
113â†’              const allMilestones = [...useAppStore.getState().milestones];
114â†’              window.electronAPI?.saveMilestones?.(store.projectDir, allMilestones).catch(() => {});
115â†’            }
116â†’          }
117â†’        },
118â†’        onSessionId: (sessionId) => {
119â†’          store.setSessionId(sessionId);
120â†’          // Persist to electron-store
121â†’          const projectId = store.activeProjectId;
122â†’          if (projectId) {
123â†’            window.electronAPI?.updateProject?.(projectId, { sessionId }).catch(() => {});
124â†’          }
125â†’        },
126â†’        onDone: () => {
127â†’          store.setStreaming(false);
128â†’        },
129â†’        onRawEvent: (raw, timestamp) => {
130â†’          useDebugStore.getState().addEvent(raw, timestamp);
131â†’        },
132â†’        onError: (err) => {
133â†’          store.setStreaming(false);
134â†’          store.addMessage({
135â†’            id: `error-${Date.now()}`,
136â†’            role: "status",
137â†’            content:
138â†’              store.locale === "he"
139â†’                ? "×¨×’×¢, ×× ×¡×” ×©×•×‘..."
140â†’                : "Hmm, let me try that again...",
141â†’            timestamp: Date.now(),
142â†’          });
143â†’        },
144â†’        callbacks: {
145â†’          onDevServerDetected: () => {
146â†’            // Poll port 3000 and set preview URL when ready
147â†’            window.electronAPI?.pollPort?.(3000).then((ready: boolean) => {
148â†’              if (ready) {
149â†’                store.setPreviewUrl("http://localhost:3000");
150â†’              }
151â†’            });
152â†’          },
153â†’          onFileChanged: () => {
154â†’            setRefreshTrigger((c) => c + 1);
155â†’          },
156â†’          onStepCompleted: (step) => {
157â†’            store.completeStep(step);
158â†’            store.setStep(step + 1, getPhaseForStep(step + 1));
159â†’          },
160â†’          onLiveUrl: (url) => {
161â†’            store.setLiveUrl(url);
162â†’            const projectId = store.activeProjectId;
163â†’            if (projectId) {
164â†’              window.electronAPI?.updateProject?.(projectId, { liveUrl: url }).catch(() => {});
165â†’            }
166â†’          },
167â†’        } satisfies AgentCallbacks,
168â†’      });
169â†’    },
170â†’    [store]
171â†’  );
172â†’
173â†’  function handleSend(message: string) {
174â†’    if (abortRef.current) {
175â†’      abortRef.current.abort();
176â†’    }
177â†’    sendToAgent(message);
178â†’  }
179â†’
180â†’  return (
181â†’    <div className="flex h-screen flex-col bg-dummy-yellow">
182â†’      {/* Header */}
183â†’      <header className="drag-region flex items-center justify-between border-b-2 border-dummy-black/10 bg-dummy-yellow px-4 py-2" style={{ paddingLeft: 80 }}>
184â†’        <div className="no-drag flex items-center gap-2">
185â†’          <ProjectDrawerToggle />
186â†’          <Logo />
187â†’        </div>
188â†’        <StepIndicator />
189â†’        <div className="no-drag flex items-center gap-2">
190â†’          <button
191â†’            onClick={() => {
192â†’              if (store.isStreaming) {
193â†’                store.setProjectDrawerOpen(true);
194â†’              } else {
195â†’                store.reset();
196â†’                router.push("/welcome");
197â†’              }
198â†’            }}
199â†’            className="rounded-md border-2 border-dummy-black/20 bg-dummy-white p-1.5 text-dummy-black/60 transition-colors hover:border-dummy-black hover:bg-dummy-black hover:text-dummy-yellow"
200â†’            aria-label="New project"
201â†’          >
202â†’            <Plus className="size-4" />
203â†’          </button>
204â†’          <LanguageToggle />
205â†’        </div>
206â†’      </header>
207â†’
208â†’      {/* Main content: chat + optional preview */}
209â†’      <div className="flex flex-1 overflow-hidden">
210â†’        {/* Chat panel */}
211â†’        <div className={`flex flex-col overflow-hidden bg-dummy-yellow-bright/30 ${store.previewUrl ? "w-1/2" : "w-full"}`}>
212â†’          <ChatPanel onSend={handleSend} showRecoveryBanner={showRecoveryBanner} />
213â†’        </div>
214â†’
215â†’        {/* Live preview (shown when dev server is running) */}
216â†’        {store.previewUrl && (
217â†’          <div className="w-1/2 border-s-2 border-dummy-black/10">
218â†’            <LivePreview refreshTrigger={refreshTrigger} />
219â†’          </div>
220â†’        )}
221â†’      </div>
222â†’
223â†’      {/* Feedback banner */}
224â†’      {store.completedSteps.includes(4) && !store.isStreaming && (
225â†’        <motion.div
226â†’          initial={{ opacity: 0, y: 20 }}
227â†’          animate={{ opacity: 1, y: 0 }}
228â†’          className="border-t-2 border-dummy-black/10 bg-dummy-white px-4 py-3 text-center"
229â†’        >
230â†’          <p className="text-sm font-medium text-dummy-black">
231â†’            {t("feedbackBanner")}
232â†’          </p>
233â†’        </motion.div>
234â†’      )}
235â†’
236â†’      <DebugTerminal />
237â†’      <ProjectDrawer />
238â†’    </div>
239â†’  );
240â†’}
241â†’

  1â†’"use client";
  2â†’
  3â†’import { create } from "zustand";
  4â†’import { persist } from "zustand/middleware";
  5â†’
  6â†’export interface ProjectMilestone {
  7â†’  id: string;
  8â†’  type: "choice" | "build" | "change" | "publish" | "other";
  9â†’  label: string;
 10â†’  timestamp: number;
 11â†’}
 12â†’
 13â†’export interface ChatMessage {
 14â†’  id: string;
 15â†’  role: "user" | "assistant" | "status";
 16â†’  content: string;
 17â†’  timestamp: number;
 18â†’  toolName?: string;
 19â†’  toolInput?: { file_path?: string; command?: string };
 20â†’  questionData?: {
 21â†’    questions: Array<{
 22â†’      question: string;
 23â†’      options: Array<{ label: string; description: string }>;
 24â†’    }>;
 25â†’  };
 26â†’}
 27â†’
 28â†’export interface ProjectMeta {
 29â†’  id: string;
 30â†’  name: string;
 31â†’  displayName: string;
 32â†’  path: string;
 33â†’  createdAt: string;
 34â†’  lastOpenedAt: string;
 35â†’  sessionId: string | null;
 36â†’  locale: string;
 37â†’  vibe: string | null;
 38â†’  audience: string | null;
 39â†’  priority: string | null;
 40â†’  designRef: string | null;
 41â†’  liveUrl: string | null;
 42â†’  githubUrl: string | null;
 43â†’}
 44â†’
 45â†’interface AppState {
 46â†’  // Session
 47â†’  sessionId: string | null;
 48â†’  projectDir: string | null;
 49â†’  activeProjectId: string | null;
 50â†’
 51â†’  // i18n
 52â†’  locale: "he" | "en";
 53â†’
 54â†’  // Progress
 55â†’  currentStep: number;
 56â†’  phase: number;
 57â†’  completedSteps: number[];
 58â†’
 59â†’  // Chat
 60â†’  messages: ChatMessage[];
 61â†’  isStreaming: boolean;
 62â†’  messagesLoaded: boolean;
 63â†’  currentActivity: string | null;
 64â†’
 65â†’  // Project
 66â†’  projectName: string | null;
 67â†’  liveUrl: string | null;
 68â†’  githubUrl: string | null;
 69â†’
 70â†’  // User
 71â†’  userName: string | null;
 72â†’  vibe: string | null;
 73â†’  audience: string | null;
 74â†’  priority: string | null;
 75â†’  designRef: string | null;
 76â†’  idea: string | null;
 77â†’
 78â†’  // Project drawer
 79â†’  projectDrawerOpen: boolean;
 80â†’  milestones: ProjectMilestone[];
 81â†’
 82â†’  // Runtime
 83â†’  runtimeReady: boolean;
 84â†’  claudeAuthenticated: boolean;
 85â†’
 86â†’  // Preview
 87â†’  previewUrl: string | null;
 88â†’  previewMode: "desktop" | "mobile";
 89â†’
 90â†’  // Actions
 91â†’  setLocale: (locale: "he" | "en") => void;
 92â†’  setSessionId: (id: string) => void;
 93â†’  setProjectDir: (dir: string) => void;
 94â†’  setStep: (step: number, phase: number) => void;
 95â†’  completeStep: (step: number) => void;
 96â†’  addMessage: (msg: ChatMessage) => void;
 97â†’  loadMessages: (msgs: ChatMessage[]) => void;
 98â†’  setStreaming: (streaming: boolean) => void;
 99â†’  setCurrentActivity: (activity: string | null) => void;
100â†’  setProjectName: (name: string) => void;
101â†’  setLiveUrl: (url: string) => void;
102â†’  setGithubUrl: (url: string) => void;
103â†’  setUserName: (name: string) => void;
104â†’  setIdea: (idea: string) => void;
105â†’  setVibe: (vibe: string) => void;
106â†’  setAudience: (audience: string) => void;
107â†’  setPriority: (priority: string) => void;
108â†’  setDesignRef: (ref: string | null) => void;
109â†’  setRuntimeReady: (ready: boolean) => void;
110â†’  setClaudeAuthenticated: (auth: boolean) => void;
111â†’  setPreviewUrl: (url: string | null) => void;
112â†’  setPreviewMode: (mode: "desktop" | "mobile") => void;
113â†’  setActiveProjectId: (id: string | null) => void;
114â†’  toggleProjectDrawer: () => void;
115â†’  setProjectDrawerOpen: (open: boolean) => void;
116â†’  addMilestone: (milestone: ProjectMilestone) => void;
117â†’  loadMilestones: (milestones: ProjectMilestone[]) => void;
118â†’  loadProject: (meta: ProjectMeta) => void;
119â†’  reset: () => void;
120â†’}
121â†’
122â†’const initialState = {
123â†’  sessionId: null,
124â†’  projectDir: null,
125â†’  activeProjectId: null as string | null,
126â†’  locale: "he" as const,
127â†’  currentStep: 1,
128â†’  phase: 0,
129â†’  completedSteps: [],
130â†’  messages: [],
131â†’  isStreaming: false,
132â†’  messagesLoaded: false,
133â†’  currentActivity: null,
134â†’  projectName: null,
135â†’  liveUrl: null,
136â†’  githubUrl: null,
137â†’  userName: null,
138â†’  vibe: null,
139â†’  audience: null,
140â†’  priority: null,
141â†’  designRef: null,
142â†’  idea: null,
143â†’  projectDrawerOpen: false,
144â†’  milestones: [] as ProjectMilestone[],
145â†’  runtimeReady: false,
146â†’  claudeAuthenticated: false,
147â†’  previewUrl: null,
148â†’  previewMode: "desktop" as const,
149â†’};
150â†’
151â†’export const useAppStore = create<AppState>()(
152â†’  persist(
153â†’    (set) => ({
154â†’      ...initialState,
155â†’
156â†’      setLocale: (locale) => set({ locale }),
157â†’      setSessionId: (sessionId) => set({ sessionId }),
158â†’      setProjectDir: (projectDir) => set({ projectDir }),
159â†’      setStep: (currentStep, phase) => set({ currentStep, phase }),
160â†’      completeStep: (step) =>
161â†’        set((state) => ({
162â†’          completedSteps: [...new Set([...state.completedSteps, step])],
163â†’        })),
164â†’      addMessage: (msg) =>
