 80â†’/**
 81â†’ * Parse a Claude CLI stream-json event into a ChatMessage.
 82â†’ *
 83â†’ * Claude CLI stream-json format:
 84â†’ * - {"type":"system","subtype":"init",...} â€” session init (skip)
 85â†’ * - {"type":"assistant","message":{"content":[{"type":"text","text":"..."}]}} â€” assistant text
 86â†’ * - {"type":"assistant","message":{"content":[{"type":"tool_use","name":"Bash","input":{...}}]}} â€” tool use
 87â†’ * - {"type":"user","message":{"content":[{"type":"tool_result",...}]}} â€” tool result (skip)
 88â†’ * - {"type":"result","subtype":"success","result":"..."} â€” final result
 89â†’ * - {"type":"error",...} â€” error
 90â†’ */
 91â†’export interface ParseResult {
 92â†’  message: ChatMessage | null;
 93â†’  /** Activity text to show in the UI, or null to clear. undefined = no change. */
 94â†’  activity?: string | null;
 95â†’}
 96â†’
 97â†’export function parseAgentEvent(
 98â†’  raw: Record<string, unknown>,
 99â†’  locale: "he" | "en",
100â†’  callbacks?: AgentCallbacks,
101â†’  skipBlocks = 0
102â†’): ParseResult {
103â†’  const type = raw.type as string;
104â†’  const now = Date.now();
105â†’
106â†’  // Skip system/init, rate_limit, user (tool_result) messages
107â†’  if (type === "system" || type === "rate_limit_event" || type === "user") {
108â†’    return { message: null };
109â†’  }
110â†’
111â†’  // Assistant message â€” contains content blocks
112â†’  if (type === "assistant") {
113â†’    const message = raw.message as Record<string, unknown> | undefined;
114â†’    if (!message) return { message: null };
115â†’
116â†’    const content = message.content as
117â†’      | Array<Record<string, unknown>>
118â†’      | undefined;
119â†’    if (!Array.isArray(content)) return { message: null };
120â†’
121â†’    // Process only NEW content blocks (skip already-processed ones)
122â†’    for (let i = skipBlocks; i < content.length; i++) {
123â†’      const block = content[i];
124â†’      const blockType = block.type as string;
125â†’
126â†’      // Text block â€” show as assistant message + detect live URLs
127â†’      if (blockType === "text") {
128â†’        const text = (block.text as string) || "";
129â†’        if (!text.trim()) continue;
130â†’
131â†’        // Detect Vercel deployment URL
132â†’        const urlMatch = text.match(/https:\/\/[\w-]+\.vercel\.app/);
133â†’        if (urlMatch && callbacks?.onLiveUrl) {
134â†’          callbacks.onLiveUrl(urlMatch[0]);
135â†’        }
136â†’
137â†’        return {
138â†’          message: {
139â†’            id: `msg-${uid()}`,
140â†’            role: "assistant",
141â†’            content: text,
142â†’            timestamp: now,
143â†’          },
144â†’        };
145â†’      }
146â†’
147â†’      // Tool use block â€” translate to friendly status
148â†’      if (blockType === "tool_use") {
149â†’        const toolName = block.name as string;
150â†’        const toolInput = block.input as
151â†’          | Record<string, unknown>
152â†’          | undefined;
153â†’
154â†’        // Bash commands â†’ friendly message + detect dev server / progress
155â†’        if (toolName === "Bash" && toolInput?.command) {
156â†’          const command = toolInput.command as string;
157â†’
158â†’          // Detect dev server start
159â†’          if (command.includes("npm run dev") || command.includes("next dev")) {
160â†’            callbacks?.onDevServerDetected?.();
161â†’          }
162â†’
163â†’          // Detect progress completion
164â†’          const progressMatch = command.match(/progress\.sh\s+complete\s+(\d+)/);
165â†’          if (progressMatch) {
166â†’            callbacks?.onStepCompleted?.(parseInt(progressMatch[1], 10));
167â†’          }
168â†’
169â†’          const friendly = matchToolMessage(command, locale);
170â†’          const statusContent = friendly || (locale === "he" ? "×‘×•× ×”..." : "Building...");
171â†’          return {
172â†’            message: {
173â†’              id: `status-${uid()}`,
174â†’              role: "status",
175â†’              content: statusContent,
176â†’              timestamp: now,
177â†’              toolName,
178â†’              toolInput: { command },
179â†’            },
180â†’            activity: statusContent,
181â†’          };
182â†’        }
183â†’
184â†’        // Write â†’ show filename + notify file change
185â†’        if (toolName === "Write" && toolInput?.file_path) {
186â†’          callbacks?.onFileChanged?.(toolInput.file_path as string);
187â†’          const fileName = basename(toolInput.file_path as string);
188â†’          const statusContent =
189â†’            locale === "he"
190â†’              ? `×™×•×¦×¨ ${fileName}...`
191â†’              : `Creating ${fileName}...`;
192â†’          return {
193â†’            message: {
194â†’              id: `status-${uid()}`,
195â†’              role: "status",
196â†’              content: statusContent,
197â†’              timestamp: now,
198â†’              toolName,
199â†’              toolInput: { file_path: toolInput.file_path as string },
200â†’            },
201â†’            activity: statusContent,
202â†’          };
203â†’        }
204â†’
205â†’        // Edit â†’ show filename + notify file change
206â†’        if (toolName === "Edit" && toolInput?.file_path) {
207â†’          callbacks?.onFileChanged?.(toolInput.file_path as string);
208â†’          const fileName = basename(toolInput.file_path as string);
209â†’          const statusContent =
210â†’            locale === "he"
211â†’              ? `...${fileName} ×¢×•×¨×š`
212â†’              : `Editing ${fileName}...`;
213â†’          return {
214â†’            message: {
215â†’              id: `status-${uid()}`,
216â†’              role: "status",
217â†’              content: statusContent,
218â†’              timestamp: now,
219â†’              toolName,
220â†’              toolInput: { file_path: toolInput.file_path as string },
221â†’            },
222â†’            activity: statusContent,
223â†’          };
224â†’        }
225â†’
226â†’        // Write/Edit without file_path (fallback)
227â†’        if (toolName === "Write" || toolName === "Edit") {
228â†’          const statusContent =
229â†’            locale === "he" ? "××‘×¦×¢ ×©×™× ×•×™×™×..." : "Making changes...";
230â†’          return {
231â†’            message: {
232â†’              id: `status-${uid()}`,
233â†’              role: "status",
234â†’              content: statusContent,
235â†’              timestamp: now,
236â†’              toolName,
237â†’            },
238â†’            activity: statusContent,
239â†’          };
240â†’        }
241â†’
242â†’        // Glob/Grep/Read â†’ skip (internal, not interesting to user)
243â†’        if (
244â†’          toolName === "Glob" ||
245â†’          toolName === "Grep" ||
246â†’          toolName === "Read"
247â†’        ) {
248â†’          return { message: null };
249â†’        }
250â†’
251â†’        // AskUserQuestion â†’ pass through as question
252â†’        if (toolName === "AskUserQuestion") {
253â†’          return {
254â†’            message: {
255â†’              id: `question-${uid()}`,
256â†’              role: "assistant",
257â†’              content: "",
258â†’              timestamp: now,
259â†’              toolName,
260â†’              questionData: toolInput as ChatMessage["questionData"],
261â†’            },
262â†’          };
263â†’        }
264â†’
265â†’        // Other tools â†’ generic status
266â†’        return { message: null };
267â†’      }
268â†’    }
269â†’
270â†’    return { message: null };
271â†’  }
272â†’
273â†’  // Final result
274â†’  if (type === "result") {
275â†’    const subtype = raw.subtype as string;
276â†’
277â†’    if (subtype === "error" || raw.is_error) {
278â†’      return {
279â†’        message: {
280â†’          id: `error-${uid()}`,
281â†’          role: "status",
282â†’          content:
283â†’            locale === "he"
284â†’              ? "×¨×’×¢, ×× ×¡×” ×©×•×‘..."
285â†’              : "Hmm, let me try that again...",
286â†’          timestamp: now,
287â†’        },
288â†’        activity: null,
289â†’      };
290â†’    }
291â†’
292â†’    // Success result â€” skip, the content was already streamed via assistant text blocks.
293â†’    // Emitting it again would cause duplicate messages.
294â†’    return { message: null, activity: null };
295â†’  }
296â†’
297â†’  // Error
298â†’  if (type === "error") {
299â†’    return {
300â†’      message: {
301â†’        id: `error-${uid()}`,
302â†’        role: "status",
303â†’        content:
304â†’          locale === "he"
305â†’            ? "×¨×’×¢, ×× ×¡×” ×©×•×‘..."
306â†’            : "Hmm, let me try that again...",
307â†’        timestamp: now,
308â†’      },
309â†’      activity: null,
310â†’    };
311â†’  }
312â†’
313â†’  return { message: null };
314â†’}
