  1â†’import { NextRequest } from "next/server";
  2â†’import { spawn } from "child_process";
  3â†’import { agentLog, createLogger } from "@/lib/logger";
  4â†’import { findClaude } from "@/lib/find-claude";
  5â†’
  6â†’const debugLog = createLogger("agent-debug");
  7â†’
  8â†’export const runtime = "nodejs";
  9â†’export const dynamic = "force-dynamic";
 10â†’
 11â†’const claudePath = findClaude();
 12â†’agentLog.info("Claude binary resolved", { path: claudePath });
 13â†’
 14â†’/**
 15â†’ * SSE endpoint that streams Claude CLI messages to the renderer.
 16â†’ * Spawns `claude --print --output-format stream-json` as a subprocess.
 17â†’ *
 18â†’ * Key: stdin must be "ignore" â€” claude CLI hangs if stdin is piped.
 19â†’ */
 20â†’export async function POST(req: NextRequest) {
 21â†’  const { prompt, locale, projectDir, sessionId } = await req.json();
 22â†’
 23â†’  const requestId = `req-${Date.now().toString(36)}`;
 24â†’  agentLog.info(`[${requestId}] New agent request`, {
 25â†’    locale,
 26â†’    sessionId: sessionId || null,
 27â†’    promptLength: prompt.length,
 28â†’    cwd: projectDir || process.cwd(),
 29â†’  });
 30â†’
 31â†’  const encoder = new TextEncoder();
 32â†’
 33â†’  const localizedPrompt =
 34â†’    locale === "he"
 35â†’      ? `[IMPORTANT: Respond in Hebrew. The user speaks Hebrew.]\n\n${prompt}`
 36â†’      : prompt;
 37â†’
 38â†’  const args = [
 39â†’    "--print",
 40â†’    "--output-format",
 41â†’    "stream-json",
 42â†’    "--dangerously-skip-permissions",
 43â†’    "--verbose",
 44â†’  ];
 45â†’
 46â†’  if (sessionId) {
 47â†’    args.push("--resume", sessionId);
 48â†’  }
 49â†’
 50â†’  args.push(localizedPrompt);
 51â†’
 52â†’  const env = { ...process.env };
 53â†’  delete env.CLAUDECODE;
 54â†’
 55â†’  const stream = new ReadableStream({
 56â†’    start(controller) {
 57â†’      agentLog.info(`[${requestId}] Spawning claude`, {
 58â†’        binary: claudePath,
 59â†’        args: args.slice(0, -1), // exclude prompt
 60â†’      });
 61â†’
 62â†’      const child = spawn(claudePath, args, {
 63â†’        cwd: projectDir || process.cwd(),
 64â†’        env,
 65â†’        stdio: ["ignore", "pipe", "pipe"],
 66â†’      });
 67â†’
 68â†’      const childPid = child.pid;
 69â†’      agentLog.info(`[${requestId}] Claude process started`, { pid: childPid });
 70â†’
 71â†’      let buffer = "";
 72â†’      let messageCount = 0;
 73â†’
 74â†’      child.stdout!.on("data", (data: Buffer) => {
 75â†’        buffer += data.toString();
 76â†’        const lines = buffer.split("\n");
 77â†’        buffer = lines.pop() || "";
 78â†’
 79â†’        for (const line of lines) {
 80â†’          if (!line.trim()) continue;
 81â†’          try {
 82â†’            const parsed = JSON.parse(line);
 83â†’            messageCount++;
 84â†’            debugLog.debug(`[${requestId}] raw`, parsed);
 85â†’
 86â†’            // Log message type and key info (not full payload â€” too large)
 87â†’            const logEntry: Record<string, unknown> = {
 88â†’              type: parsed.type,
 89â†’              seq: messageCount,
 90â†’            };
 91â†’            if (parsed.type === "assistant" && parsed.message?.content) {
 92â†’              const content = parsed.message.content;
 93â†’              if (Array.isArray(content)) {
 94â†’                logEntry.blocks = content.map((b: Record<string, unknown>) => {
 95â†’                  if (b.type === "text") return { type: "text", length: (b.text as string)?.length };
 96â†’                  if (b.type === "tool_use") return { type: "tool_use", name: b.name };
 97â†’                  return { type: b.type };
 98â†’                });
 99â†’              }
100â†’            }
101â†’            if (parsed.type === "result") {
102â†’              logEntry.subtype = parsed.subtype;
103â†’              logEntry.duration_ms = parsed.duration_ms;
104â†’              logEntry.num_turns = parsed.num_turns;
105â†’              logEntry.cost_usd = parsed.total_cost_usd;
106â†’              logEntry.session_id = parsed.session_id;
107â†’            }
108â†’            agentLog.debug(`[${requestId}] SSE event`, logEntry);
109â†’
110â†’            controller.enqueue(encoder.encode(`data: ${line}\n\n`));
111â†’          } catch {
112â†’            agentLog.warn(`[${requestId}] Non-JSON stdout line`, {
113â†’              preview: line.slice(0, 200),
114â†’            });
115â†’          }
116â†’        }
117â†’      });
118â†’
119â†’      child.stderr!.on("data", (data: Buffer) => {
120â†’        const text = data.toString().trim();
121â†’        if (!text) return;
122â†’        agentLog.warn(`[${requestId}] Claude stderr`, { text: text.slice(0, 500) });
123â†’        if (text.includes("Error") || text.includes("error")) {
124â†’          controller.enqueue(
125â†’            encoder.encode(
126â†’              `data: ${JSON.stringify({ type: "error", content: text })}\n\n`
127â†’            )
128â†’          );
129â†’        }
130â†’      });
131â†’
132â†’      child.on("error", (err) => {
133â†’        agentLog.error(`[${requestId}] Spawn error`, { error: err.message });
134â†’        controller.enqueue(
135â†’          encoder.encode(
136â†’            `data: ${JSON.stringify({ type: "error", content: err.message })}\n\n`
137â†’          )
138â†’        );
139â†’        controller.enqueue(encoder.encode("data: [DONE]\n\n"));
140â†’        controller.close();
141â†’      });
142â†’
143â†’      child.on("close", (code, signal) => {
144â†’        agentLog.info(`[${requestId}] Claude process exited`, {
145â†’          pid: childPid,
146â†’          code,
147â†’          signal,
148â†’          messageCount,
149â†’        });
150â†’        if (buffer.trim()) {
151â†’          try {
152â†’            JSON.parse(buffer);
153â†’            controller.enqueue(encoder.encode(`data: ${buffer}\n\n`));
154â†’          } catch {
155â†’            // ignore
156â†’          }
157â†’        }
158â†’        controller.enqueue(encoder.encode("data: [DONE]\n\n"));
159â†’        controller.close();
160â†’      });
161â†’
162â†’      req.signal.addEventListener("abort", () => {
163â†’        agentLog.info(`[${requestId}] Client disconnected, killing claude`, {
164â†’          pid: childPid,
165â†’        });
166â†’        child.kill("SIGTERM");
167â†’      });
168â†’    },
169â†’  });
170â†’
171â†’  return new Response(stream, {
172â†’    headers: {
173â†’      "Content-Type": "text/event-stream",
174â†’      "Cache-Control": "no-cache, no-transform",
175â†’      Connection: "keep-alive",
176â†’      "X-Accel-Buffering": "no",
177â†’    },
178â†’  });
179â†’}
