  1â†’"use client";
  2â†’
  3â†’import { useState, useEffect } from "react";
  4â†’import { useRouter } from "next/navigation";
  5â†’import { useTranslations } from "next-intl";
  6â†’import { motion } from "framer-motion";
  7â†’import { FolderOpen, Rocket } from "lucide-react";
  8â†’import MascotImage from "@/components/brand/MascotImage";
  9â†’import { Button } from "@/components/ui/button";
 10â†’import { useAppStore, type ProjectMeta } from "@/lib/store";
 11â†’
 12â†’function relativeTime(
 13â†’  dateStr: string,
 14â†’  // eslint-disable-next-line @typescript-eslint/no-explicit-any
 15â†’  t: any
 16â†’): string {
 17â†’  const diff = Date.now() - new Date(dateStr).getTime();
 18â†’  const days = Math.floor(diff / (1000 * 60 * 60 * 24));
 19â†’  if (days === 0) return t("justNow");
 20â†’  if (days === 1) return t("yesterday");
 21â†’  return t("daysAgo", { count: days });
 22â†’}
 23â†’
 24â†’export default function WelcomePage() {
 25â†’  const t = useTranslations("Welcome");
 26â†’  const tp = useTranslations("Project");
 27â†’  const router = useRouter();
 28â†’  const store = useAppStore();
 29â†’
 30â†’  const [recentProjects, setRecentProjects] = useState<ProjectMeta[]>([]);
 31â†’  const [isCreating, setIsCreating] = useState(false);
 32â†’
 33â†’  // Load recent projects on mount
 34â†’  useEffect(() => {
 35â†’    window.electronAPI
 36â†’      ?.listProjects?.()
 37â†’      .then((projects: ProjectMeta[]) => {
 38â†’        if (projects?.length) setRecentProjects(projects);
 39â†’      })
 40â†’      .catch(() => {});
 41â†’  }, []);
 42â†’
 43â†’  async function handleStartBuilding() {
 44â†’    if (isCreating) return;
 45â†’    setIsCreating(true);
 46â†’
 47â†’    try {
 48â†’      const meta = await window.electronAPI?.createProject?.("", store.locale);
 49â†’      if (meta) {
 50â†’        store.setProjectDir(meta.path);
 51â†’        store.setActiveProjectId(meta.id);
 52â†’        store.setProjectName(meta.name);
 53â†’      }
 54â†’    } catch (err) {
 55â†’      console.error("[welcome] Project creation failed:", err);
 56â†’    }
 57â†’
 58â†’    // Start at step 2 (orient/greet) â€” let the CC4D skill handle planning
 59â†’    store.setStep(2, 0);
 60â†’    router.push("/build");
 61â†’  }
 62â†’
 63â†’  async function handleProjectSwitch(project: ProjectMeta) {
 64â†’    try {
 65â†’      const meta = await window.electronAPI?.switchProject?.(project.id);
 66â†’      if (!meta) return;
 67â†’      store.loadProject(meta);
 68â†’      if (meta.sessionId) {
 69â†’        store.setStep(4, 1);
 70â†’      }
 71â†’      router.push("/build");
 72â†’    } catch (err) {
 73â†’      console.error("[welcome] Project switch failed:", err);
 74â†’    }
 75â†’  }
 76â†’
 77â†’  return (
 78â†’    <div className="flex min-h-screen flex-col items-center justify-center bg-dummy-yellow p-8">
 79â†’      {/* Mascot */}
 80â†’      <motion.div
 81â†’        initial={{ opacity: 0, scale: 0.9 }}
 82â†’        animate={{ opacity: 1, scale: 1 }}
 83â†’        transition={{ duration: 0.4 }}
 84â†’        className="mb-6"
 85â†’      >
 86â†’        <MascotImage
 87â†’          pose="waving"
 88â†’          alt="Mascot"
 89â†’          width={180}
 90â†’          height={180}
 91â†’          className="rotate-2"
 92â†’        />
 93â†’      </motion.div>
 94â†’
 95â†’      {/* Content */}
 96â†’      <motion.div
 97â†’        initial={{ opacity: 0, y: 20 }}
 98â†’        animate={{ opacity: 1, y: 0 }}
 99â†’        className="w-full max-w-lg text-center"
100â†’      >
101â†’        {/* Recent projects */}
102â†’        {recentProjects.length > 0 && (
103â†’          <div className="mb-6">
104â†’            <p className="mb-3 text-sm font-bold text-dummy-black/70">
105â†’              {t("recentProjects")}
106â†’            </p>
107â†’            <div className="flex gap-3 overflow-x-auto pb-2">
108â†’              {recentProjects.map((project) => (
109â†’                <motion.button
110â†’                  key={project.id}
111â†’                  whileHover={{ scale: 1.03 }}
112â†’                  whileTap={{ scale: 0.97 }}
113â†’                  onClick={() => handleProjectSwitch(project)}
114â†’                  className="flex shrink-0 items-center gap-2 rounded-xl border-2 border-dummy-black/20 bg-dummy-white px-4 py-2 text-start transition-all hover:border-dummy-black hover:shadow-[4px_4px_0_0_#1A1A1A]"
115â†’                >
116â†’                  <FolderOpen className="size-4 shrink-0 text-dummy-black/60" />
117â†’                  <div>
118â†’                    <p className="text-sm font-bold text-dummy-black line-clamp-1">
119â†’                      {project.displayName || project.name}
120â†’                    </p>
121â†’                    <p className="text-xs text-dummy-black/50">
122â†’                      {relativeTime(project.lastOpenedAt, tp)}
123â†’                    </p>
124â†’                  </div>
125â†’                </motion.button>
126â†’              ))}
127â†’            </div>
128â†’          </div>
129â†’        )}
130â†’
131â†’        <h1 className="mb-2 font-[family-name:var(--font-display)] text-3xl text-dummy-black">
132â†’          {t("greeting")}
133â†’        </h1>
134â†’        <h2 className="mb-6 text-xl font-bold text-dummy-black">
