316â†’/**
317â†’ * Connect to the agent SSE stream.
318â†’ */
319â†’export function connectToAgent(options: {
320â†’  prompt: string;
321â†’  locale: "he" | "en";
322â†’  projectDir?: string;
323â†’  sessionId?: string;
324â†’  onMessage: (msg: ChatMessage) => void;
325â†’  onDone: () => void;
326â†’  onError: (err: string) => void;
327â†’  onSessionId?: (sessionId: string) => void;
328â†’  onRawEvent?: (raw: Record<string, unknown>, timestamp: number) => void;
329â†’  callbacks?: AgentCallbacks;
330â†’}): { abort: () => void } {
331â†’  const controller = new AbortController();
332â†’
333â†’  const body = JSON.stringify({
334â†’    prompt: options.prompt,
335â†’    locale: options.locale,
336â†’    projectDir: options.projectDir,
337â†’    sessionId: options.sessionId,
338â†’  });
339â†’
340â†’  fetch("/api/agent", {
341â†’    method: "POST",
342â†’    headers: { "Content-Type": "application/json" },
343â†’    body,
344â†’    signal: controller.signal,
345â†’  })
346â†’    .then(async (res) => {
347â†’      if (!res.ok || !res.body) {
348â†’        options.onError(`HTTP ${res.status}`);
349â†’        return;
350â†’      }
351â†’
352â†’      const reader = res.body.getReader();
353â†’      const decoder = new TextDecoder();
354â†’      let buffer = "";
355â†’      let lastAssistantBlockCount = 0;
356â†’
357â†’      while (true) {
358â†’        const { done, value } = await reader.read();
359â†’        if (done) break;
360â†’
361â†’        buffer += decoder.decode(value, { stream: true });
362â†’        const lines = buffer.split("\n");
363â†’        buffer = lines.pop() || "";
364â†’
365â†’        for (const line of lines) {
366â†’          if (line.startsWith("data: ")) {
367â†’            const data = line.slice(6);
368â†’            if (data === "[DONE]") {
369â†’              useAppStore.getState().setCurrentActivity(null);
370â†’              options.onDone();
371â†’              return;
372â†’            }
373â†’            try {
374â†’              const parsed = JSON.parse(data);
375â†’              options.onRawEvent?.(parsed, Date.now());
376â†’              // Capture session_id from result events
377â†’              if (parsed.session_id && options.onSessionId) {
378â†’                options.onSessionId(parsed.session_id as string);
379â†’              }
380â†’              // Track block count for dedup: assistant events are cumulative,
381â†’              // so we skip already-processed content blocks
382â†’              const skipBlocks = (parsed.type === "assistant") ? lastAssistantBlockCount : 0;
383â†’              const { message: msg, activity } = parseAgentEvent(parsed, options.locale, options.callbacks, skipBlocks);
384â†’
385â†’              // Update block count after processing
386â†’              if (parsed.type === "assistant") {
387â†’                const content = (parsed.message as Record<string, unknown> | undefined)?.content;
388â†’                lastAssistantBlockCount = Array.isArray(content) ? content.length : 0;
389â†’              } else {
390â†’                lastAssistantBlockCount = 0;
391â†’              }
392â†’
393â†’              if (activity !== undefined) {
394â†’                useAppStore.getState().setCurrentActivity(activity);
395â†’              }
396â†’              if (msg) options.onMessage(msg);
397â†’            } catch {
398â†’              // Skip unparseable lines
399â†’            }
400â†’          }
401â†’        }
402â†’      }
403â†’
404â†’      useAppStore.getState().setCurrentActivity(null);
405â†’      options.onDone();
406â†’    })
407â†’    .catch((err) => {
408â†’      if (err.name !== "AbortError") {
409â†’        useAppStore.getState().setCurrentActivity(null);
410â†’        options.onError(err.message);
411â†’      }
412â†’    });
413â†’
414â†’  return {
415â†’    abort: () => controller.abort(),
416â†’  };
417â†’}
