  1â†’import type { ChatMessage, ProjectMilestone } from "./store";
  2â†’
  3â†’function uid(): string {
  4â†’  return `ms-${Date.now()}-${Math.random().toString(36).slice(2, 6)}`;
  5â†’}
  6â†’
  7â†’/**
  8â†’ * Examine an incoming ChatMessage and extract a milestone if it represents
  9â†’ * a noteworthy event (choice, build action, publish, etc.).
 10â†’ * Returns null when the message isn't milestone-worthy.
 11â†’ */
 12â†’export function extractMilestone(
 13â†’  msg: ChatMessage,
 14â†’  locale: "he" | "en"
 15â†’): ProjectMilestone | null {
 16â†’  const now = msg.timestamp || Date.now();
 17â†’
 18â†’  // Tool-based milestones
 19â†’  if (msg.toolName) {
 20â†’    const command = msg.toolInput?.command || "";
 21â†’
 22â†’    // Scaffolding / creating project
 23â†’    if (command.includes("create-next-app") || command.includes("npm init")) {
 24â†’      return {
 25â†’        id: uid(),
 26â†’        type: "build",
 27â†’        label: locale === "he" ? "×”×¤×¨×•×™×§×˜ × ×•×¦×¨" : "Project created",
 28â†’        timestamp: now,
 29â†’      };
 30â†’    }
 31â†’
 32â†’    // Dev server started
 33â†’    if (command.includes("npm run dev") || command.includes("next dev")) {
 34â†’      return {
 35â†’        id: uid(),
 36â†’        type: "build",
 37â†’        label: locale === "he" ? "×’×¨×¡×” ×¨××©×•× ×” ××•×›× ×”" : "First version built",
 38â†’        timestamp: now,
 39â†’      };
 40â†’    }
 41â†’
 42â†’    // Publishing
 43â†’    if (command.includes("vercel") || command.includes("deploy")) {
 44â†’      return {
 45â†’        id: uid(),
 46â†’        type: "publish",
 47â†’        label: locale === "he" ? "×”××ª×¨ ×¤×•×¨×¡×" : "Site published",
 48â†’        timestamp: now,
 49â†’      };
 50â†’    }
 51â†’
 52â†’    // File writes/edits â€” aggregate as "Made changes"
 53â†’    if (msg.toolName === "Write" || msg.toolName === "Edit") {
 54â†’      const fileName = msg.toolInput?.file_path?.split("/").pop() || "";
 55â†’      return {
 56â†’        id: uid(),
 57â†’        type: "change",
 58â†’        label:
 59â†’          locale === "he"
 60â†’            ? `×¢×¨×™×›×”: ${fileName}`
 61â†’            : `Edited: ${fileName}`,
 62â†’        timestamp: now,
 63â†’      };
 64â†’    }
 65â†’
 66â†’    return null;
 67â†’  }
 68â†’
 69â†’  // Assistant text milestones â€” look for key phrases
 70â†’  if (msg.role === "assistant" && msg.content) {
 71â†’    const text = msg.content.toLowerCase();
 72â†’
 73â†’    // Detect live URL announcement
 74â†’    if (text.includes("vercel.app") || text.includes("your site is live")) {
 75â†’      return {
 76â†’        id: uid(),
 77â†’        type: "publish",
 78â†’        label: locale === "he" ? "×”××ª×¨ ×‘××•×•×™×¨!" : "Site is live!",
 79â†’        timestamp: now,
 80â†’      };
 81â†’    }
 82â†’
 83â†’    return null;
 84â†’  }
 85â†’
 86â†’  return null;
 87â†’}
 88â†’
 89â†’/**
 90â†’ * Throttle file-change milestones so we don't flood the log with
 91â†’ * "Edited: foo.tsx" for every single file. Groups edits within a
 92â†’ * 10-second window into a single "Made changes" entry.
 93â†’ */
 94â†’export function createMilestoneThrottler(locale: "he" | "en") {
 95â†’  let lastFileChangeMilestone = 0;
 96â†’  const FILE_CHANGE_COOLDOWN = 10_000; // 10 seconds
 97â†’
 98â†’  return function maybeExtract(msg: ChatMessage): ProjectMilestone | null {
 99â†’    const milestone = extractMilestone(msg, locale);
100â†’    if (!milestone) return null;
101â†’
102â†’    // Throttle file changes
103â†’    if (milestone.type === "change") {
104â†’      const now = Date.now();
105â†’      if (now - lastFileChangeMilestone < FILE_CHANGE_COOLDOWN) {
106â†’        return null;
107â†’      }
108â†’      lastFileChangeMilestone = now;
109â†’      // Generalize throttled edits
110â†’      milestone.label = locale === "he" ? "×‘×•×¦×¢×• ×©×™× ×•×™×™×" : "Made changes";
111â†’    }
112â†’
113â†’    return milestone;
114â†’  };
115â†’}
