# Assign Google Auth (THE-12) to v0.2.0 milestone
V020_MILESTONE="f4c4802d-2d16-480a-9892-9e3ec44681c5"
GOOGLE_AUTH_ID="18ca4a41-e41c-4d0f-bf5b-e4084d068316"

linear api --variable issueId="$GOOGLE_AUTH_ID" --variable milestoneId="$V020_MILESTONE" <<'GRAPHQL'
mutation($issueId: String!, $milestoneId: String!) {
  issueUpdate(id: $issueId, input: { projectMilestoneId: $milestoneId }) {
    success
    issue { identifier title }
  }
}
GRAPHQL
