156→          const command = toolInput.command as string;
157→
158→          // Detect dev server start
159→          if (command.includes("npm run dev") || command.includes("next dev")) {
160→            callbacks?.onDevServerDetected?.();
161→          }
162→
163→          // Detect progress completion
164→          const progressMatch = command.match(/progress\.sh\s+complete\s+(\d+)/);
165→          if (progressMatch) {
166→            callbacks?.onStepCompleted?.(parseInt(progressMatch[1], 10));
167→          }
168→
169→          const friendly = matchToolMessage(command, locale);
170→          const statusContent = friendly || (locale === "he" ? "בונה..." : "Building...");
171→          return {
172→            message: {
173→              id: `status-${uid()}`,
174→              role: "status",
175→              content: statusContent,
176→              timestamp: now,
177→              toolName,
178→              toolInput: { command },
179→            },
180→            activity: statusContent,
181→          };
182→        }
