316→/**
317→ * Connect to the agent SSE stream.
318→ */
319→export function connectToAgent(options: {
320→  prompt: string;
321→  images?: ImageAttachment[];
322→  locale: "he" | "en";
323→  projectDir?: string;
324→  sessionId?: string;
325→  onMessage: (msg: ChatMessage) => void;
326→  onDone: () => void;
327→  onError: (err: string) => void;
328→  onSessionId?: (sessionId: string) => void;
329→  onRawEvent?: (raw: Record<string, unknown>, timestamp: number) => void;
330→  callbacks?: AgentCallbacks;
331→}): { abort: () => void } {
332→  const controller = new AbortController();
333→
334→  const body = JSON.stringify({
335→    prompt: options.prompt,
336→    images: options.images,
337→    locale: options.locale,
338→    projectDir: options.projectDir,
339→    sessionId: options.sessionId,
340→  });
341→
342→  fetch("/api/agent", {
343→    method: "POST",
344→    headers: { "Content-Type": "application/json" },
345→    body,
346→    signal: controller.signal,
347→  })
348→    .then(async (res) => {
349→      if (!res.ok || !res.body) {
350→        options.onError(`HTTP ${res.status}`);
351→        return;
352→      }
353→
354→      const reader = res.body.getReader();
355→      const decoder = new TextDecoder();
356→      let buffer = "";
357→      let lastAssistantBlockCount = 0;
358→
359→      while (true) {
360→        const { done, value } = await reader.read();
361→        if (done) break;
362→
363→        buffer += decoder.decode(value, { stream: true });
364→        const lines = buffer.split("\n");
365→        buffer = lines.pop() || "";
366→
367→        for (const line of lines) {
368→          if (line.startsWith("data: ")) {
369→            const data = line.slice(6);
370→            if (data === "[DONE]") {
371→              useAppStore.getState().setCurrentActivity(null);
372→              options.onDone();
373→              return;
374→            }
375→            try {
376→              const parsed = JSON.parse(data);
377→              options.onRawEvent?.(parsed, Date.now());
378→              // Capture session_id from result events
379→              if (parsed.session_id && options.onSessionId) {
380→                options.onSessionId(parsed.session_id as string);
381→              }
382→              // Track block count for dedup: assistant events are cumulative,
383→              // so we skip already-processed content blocks
384→              const skipBlocks = (parsed.type === "assistant") ? lastAssistantBlockCount : 0;
385→              const { message: msg, activity } = parseAgentEvent(parsed, options.locale, options.callbacks, skipBlocks);
386→
387→              // Update block count after processing
388→              if (parsed.type === "assistant") {
389→                const content = (parsed.message as Record<string, unknown> | undefined)?.content;
390→                lastAssistantBlockCount = Array.isArray(content) ? content.length : 0;
391→              } else {
392→                lastAssistantBlockCount = 0;
393→              }
394→
395→              if (activity !== undefined) {
396→                useAppStore.getState().setCurrentActivity(activity);
397→              }
398→              if (msg) options.onMessage(msg);
399→            } catch {
400→              // Skip unparseable lines
401→            }
402→          }
403→        }
404→      }
405→
406→      useAppStore.getState().setCurrentActivity(null);
407→      options.onDone();
408→    })
409→    .catch((err) => {
410→      if (err.name !== "AbortError") {
411→        useAppStore.getState().setCurrentActivity(null);
412→        options.onError(err.message);
413→      }
414→    });
415→
416→  return {
417→    abort: () => controller.abort(),
418→  };
419→}
