  1→const { app, ipcMain, shell, dialog } = require("electron");
  2→const { execSync } = require("child_process");
  3→const path = require("path");
  4→const fs = require("fs");
  5→const net = require("net");
  6→const log = require("electron-log/main");
  7→const { GitProcess } = require("dugite");
  8→const projectStore = require("./project-store.mjs");
  9→const { slugifyIdea, deduplicatePath } = require("./project-utils");
 10→const { findClaude } = require("./find-claude");
 11→const { copySkillFiles, writeProjectClaudeMd, initProgressState } = require("./skill-files");
 12→
 13→const LOG_DIR = path.join(require("os").homedir(), ".claude-for-beginners", "logs");
 14→
 15→function getProjectsDir() {
 16→  const home = app.getPath("home");
 17→  return path.join(home, "Documents", "Claude Projects");
 18→}
 19→
 20→// --- Atomic JSON file helpers ---
 21→
 22→function loadJsonFile(filePath, fallback) {
 23→  try {
 24→    const data = fs.readFileSync(filePath, "utf-8");
 25→    return JSON.parse(data);
 26→  } catch {
 27→    return fallback;
 28→  }
 29→}
 30→
 31→function saveJsonFileAtomic(filePath, data) {
 32→  const dir = path.dirname(filePath);
 33→  fs.mkdirSync(dir, { recursive: true });
 34→  const tmpPath = filePath + ".tmp";
 35→  fs.writeFileSync(tmpPath, JSON.stringify(data), "utf-8");
 36→  fs.renameSync(tmpPath, filePath);
 37→}
 38→
 39→// --- IPC registration ---
 40→
 41→function setupIPC() {
 42→  // --- System ---
 43→
 44→  ipcMain.handle("locale:get", () => {
 45→    const osLocale = app.getLocale();
 46→    const locale = osLocale.startsWith("he") ? "he" : "en";
 47→    log.info("Locale requested:", osLocale, "→", locale);
 48→    return locale;
 49→  });
 50→
 51→  ipcMain.handle("runtime:status", async () => {
 52→    let nodeReady = false;
 53→    try {
 54→      const nodeVersion = execSync("node --version", { encoding: "utf-8", timeout: 5000 }).trim();
 55→      const major = parseInt(nodeVersion.replace("v", "").split(".")[0], 10);
 56→      nodeReady = major >= 18;
 57→    } catch {}
 58→
 59→    let gitReady = false;
 60→    try {
 61→      execSync("git --version", { timeout: 5000 });
 62→      gitReady = true;
 63→    } catch {}
 64→
 65→    const claudeReady = findClaude() !== null;
 66→
 67→    const status = { nodeReady, gitReady, claudeReady };
 68→    log.debug("Runtime status:", status);
 69→    return status;
 70→  });
 71→
 72→  ipcMain.handle("project:open", (_event, projectPath) => {
 73→    log.info("Opening project path:", projectPath);
 74→    shell.openPath(projectPath);
 75→  });
 76→
 77→  ipcMain.handle("shell:open-external", (_event, url) => {
 78→    log.info("Opening external:", url);
 79→    shell.openExternal(url);
 80→  });
 81→
 82→  ipcMain.handle("log:get-path", () => {
 83→    return log.transports.file.getFile().path;
 84→  });
 85→
 86→  // --- Renderer log forwarding ---
 87→
 88→  const rendererLogPath = path.join(LOG_DIR, "renderer.log");
 89→  const RENDERER_MAX_SIZE = 5 * 1024 * 1024; // 5 MB
 90→
 91→  ipcMain.on("log:from-renderer", (_event, { level, message }) => {
 92→    const line = `[${new Date().toISOString()}] [${level.toUpperCase()}] ${message}\n`;
 93→    try {
 94→      // Simple size-based rotation
 95→      try {
 96→        const stat = fs.statSync(rendererLogPath);
 97→        if (stat.size >= RENDERER_MAX_SIZE) {
 98→          const oldPath = rendererLogPath + ".old";
 99→          try { fs.unlinkSync(oldPath); } catch {}
100→          fs.renameSync(rendererLogPath, oldPath);
101→        }
102→      } catch {}
103→      fs.appendFileSync(rendererLogPath, line);
104→    } catch {}
105→  });
106→
107→  // --- Project management ---
108→
109→  ipcMain.handle("project:create", async (_event, { idea, locale }) => {
110→    try {
111→      const baseDir = getProjectsDir();
112→      fs.mkdirSync(baseDir, { recursive: true });
113→
114→      const hasIdea = idea && idea.trim();
115→      const slug = hasIdea ? slugifyIdea(idea) : "new-project";
116→      const name = await deduplicatePath(baseDir, slug);
117→      const projectPath = path.join(baseDir, name);
118→
119→      fs.mkdirSync(projectPath, { recursive: true });
120→      log.info("[project] Created directory:", projectPath);
121→
122→      // git init
123→      const gitResult = await GitProcess.exec(["init"], projectPath);
124→      if (gitResult.exitCode !== 0) {
125→        log.warn("[project] git init warning:", gitResult.stderr);
126→      } else {
127→        log.info("[project] git init OK in", projectPath);
128→      }
129→
130→      // Copy skill files, write CLAUDE.md, initialize progress
131→      copySkillFiles(projectPath);
132→      writeProjectClaudeMd(projectPath, hasIdea ? { idea: idea.trim() } : null);
133→      initProgressState(projectPath);
134→
135→      const now = new Date().toISOString();
136→      const meta = {
137→        id: `proj-${Date.now()}-${Math.random().toString(36).slice(2, 6)}`,
138→        name,
139→        displayName: hasIdea ? idea.trim().slice(0, 80) : "",
140→        path: projectPath,
141→        createdAt: now,
142→        lastOpenedAt: now,
143→        sessionId: null,
144→        locale: locale || "he",
145→        vibe: null,
146→        audience: null,
147→        priority: null,
148→        designRef: null,
149→        liveUrl: null,
150→        githubUrl: null,
151→      };
152→
153→      projectStore.addProject(meta);
154→      log.info("[project] Created", name, "at", projectPath);
155→      return meta;
156→    } catch (err) {
157→      log.error("[project] Create failed:", err.message);
158→      throw err;
159→    }
160→  });
161→
162→  ipcMain.handle("project:list", () => {
163→    const projects = projectStore.getProjects();
164→    return projects.sort(
165→      (a, b) => new Date(b.lastOpenedAt).getTime() - new Date(a.lastOpenedAt).getTime()
166→    );
167→  });
168→
169→  ipcMain.handle("project:switch", (_event, id) => {
170→    const project = projectStore.getProject(id);
171→    if (!project) {
172→      log.warn("[project] Switch failed — not found:", id);
173→      return null;
174→    }
175→    if (!fs.existsSync(project.path)) {
176→      log.warn("[project] Switch failed — dir missing:", project.path);
177→      return null;
178→    }
179→    projectStore.updateProject(id, { lastOpenedAt: new Date().toISOString() });
180→    projectStore.setActiveProjectId(id);
181→    log.info("[project] Switched to", project.name);
182→    return projectStore.getProject(id);
183→  });
184→
185→  ipcMain.handle("project:update", (_event, { id, updates }) => {
186→    const updated = projectStore.updateProject(id, updates);
187→    if (updated) {
188→      log.info("[project] Updated", id, Object.keys(updates).join(", "));
189→    }
190→    return updated;
191→  });
192→
193→  ipcMain.handle("project:get-active", () => {
194→    return projectStore.getActiveProject();
195→  });
196→
197→  // --- Chat history persistence ---
198→
199→  ipcMain.handle("chat:load", async (_event, projectPath) => {
200→    const filePath = path.join(projectPath, ".cc4d", "chat-history.json");
201→    return loadJsonFile(filePath, []);
202→  });
203→
204→  ipcMain.handle("chat:save", async (_event, { projectPath, messages }) => {
205→    try {
206→      const filePath = path.join(projectPath, ".cc4d", "chat-history.json");
207→      saveJsonFileAtomic(filePath, messages);
208→      return true;
209→    } catch (err) {
210→      log.error("[chat] Save failed:", err.message);
211→      return false;
212→    }
213→  });
214→
215→  // --- Finalize preferences ---
216→
217→  ipcMain.handle("project:finalize-preferences", async (_event, { projectId, preferences }) => {
218→    try {
219→      const project = projectStore.getProject(projectId);
220→      if (!project) {
221→        log.warn("[project] finalize-preferences: project not found", projectId);
222→        return false;
223→      }
224→
225→      const projectPath = project.path;
226→
227→      // Re-write CLAUDE.md with full preferences
228→      writeProjectClaudeMd(projectPath, preferences);
229→
230→      // Update progress to step 4
231→      const state = {
232→        current_step: 4,
233→        completed: [1, 2, 3],
234→        phase: 1,
235→      };
236→      const statePath = path.join(projectPath, ".cc4d-progress.json");
237→      fs.writeFileSync(statePath, JSON.stringify(state, null, 2), "utf-8");
238→
239→      // Persist preferences to electron-store
240→      projectStore.updateProject(projectId, {
241→        vibe: preferences.vibe || null,
242→        audience: preferences.audience || null,
243→        priority: preferences.priority || null,
244→        designRef: preferences.designRef || null,
245→      });
246→
247→      log.info("[project] Finalized preferences for", project.name);
248→      return true;
249→    } catch (err) {
250→      log.error("[project] finalize-preferences failed:", err.message);
251→      return false;
252→    }
253→  });
254→
255→  // --- Milestones persistence ---
256→
257→  ipcMain.handle("milestones:load", async (_event, projectPath) => {
258→    const filePath = path.join(projectPath, ".cc4d", "milestones.json");
259→    return loadJsonFile(filePath, []);
260→  });
261→
262→  ipcMain.handle("milestones:save", async (_event, { projectPath, milestones }) => {
263→    try {
264→      const filePath = path.join(projectPath, ".cc4d", "milestones.json");
265→      saveJsonFileAtomic(filePath, milestones);
266→      return true;
267→    } catch (err) {
268→      log.error("[milestones] Save failed:", err.message);
269→      return false;
270→    }
271→  });
272→
273→  // --- Preview port polling ---
274→
275→  ipcMain.handle("preview:poll-port", async (_event, port) => {
276→    const timeout = 30000;
277→    const start = Date.now();
278→    while (Date.now() - start < timeout) {
279→      try {
280→        await new Promise((resolve, reject) => {
281→          const conn = net.createConnection({ port, host: "127.0.0.1" }, () => {
282→            conn.end();
283→            resolve(true);
284→          });
285→          conn.on("error", reject);
286→          conn.setTimeout(1000, () => {
287→            conn.destroy();
288→            reject(new Error("timeout"));
289→          });
290→        });
291→        return true;
292→      } catch {
293→        await new Promise((r) => setTimeout(r, 500));
294→      }
295→    }
296→    return false;
297→  });
298→
299→  // --- Inspirations ---
300→
301→  ipcMain.handle("inspirations:load", async (_event, projectPath) => {
302→    const filePath = path.join(projectPath, ".cc4d", "inspirations.json");
303→    return loadJsonFile(filePath, []);
304→  });
305→
306→  ipcMain.handle("inspirations:save", async (_event, { projectPath, urls }) => {
307→    try {
308→      const filePath = path.join(projectPath, ".cc4d", "inspirations.json");
309→      saveJsonFileAtomic(filePath, urls);
310→      return true;
311→    } catch (err) {
312→      log.error("[inspirations] Save failed:", err.message);
313→      return false;
314→    }
315→  });
316→
317→  // --- Services auth status ---
318→
319→  ipcMain.handle("services:check-auth", async () => {
320→    let github = false;
321→    let vercel = false;
322→    let vercelUser = null;
323→    try {
324→      execSync("gh auth status", { encoding: "utf-8", timeout: 10000, stdio: "pipe" });
325→      github = true;
326→    } catch {}
327→    try {
328→      const out = execSync("vercel whoami", { encoding: "utf-8", timeout: 10000, stdio: "pipe" }).trim();
329→      if (out && !out.includes("Error") && !out.includes("not logged in")) {
330→        vercel = true;
331→        vercelUser = out;
332→      }
333→    } catch {}
334→    return { github, vercel, vercelUser };
335→  });
336→
337→  // --- Environment variables ---
338→
339→  ipcMain.handle("env:load", async (_event, projectPath) => {
340→    const filePath = path.join(projectPath, ".env.local");
341→    try {
342→      const content = fs.readFileSync(filePath, "utf-8");
343→      const vars = [];
344→      for (const line of content.split("\n")) {
345→        const trimmed = line.trim();
346→        if (!trimmed || trimmed.startsWith("#")) continue;
347→        const eqIdx = trimmed.indexOf("=");
348→        if (eqIdx === -1) continue;
349→        vars.push({
350→          key: trimmed.slice(0, eqIdx),
351→          value: trimmed.slice(eqIdx + 1),
352→        });
353→      }
354→      return vars;
355→    } catch {
356→      return [];
357→    }
358→  });
359→
360→  ipcMain.handle("env:save", async (_event, { projectPath, vars }) => {
361→    try {
362→      const filePath = path.join(projectPath, ".env.local");
363→      const content = vars
364→        .filter((v) => v.key.trim())
365→        .map((v) => `${v.key}=${v.value}`)
366→        .join("\n");
367→      fs.mkdirSync(path.dirname(filePath), { recursive: true });
368→      fs.writeFileSync(filePath, content + "\n", "utf-8");
369→      return true;
370→    } catch (err) {
371→      log.error("[env] Save failed:", err.message);
372→      return false;
373→    }
374→  });
375→
376→  // --- Notes ---
377→
378→  ipcMain.handle("notes:load", async (_event, projectPath) => {
379→    const filePath = path.join(projectPath, ".cc4d", "notes.md");
380→    try {
381→      return fs.readFileSync(filePath, "utf-8");
382→    } catch {
383→      return "";
384→    }
385→  });
386→
387→  ipcMain.handle("notes:save", async (_event, { projectPath, text }) => {
388→    try {
389→      const filePath = path.join(projectPath, ".cc4d", "notes.md");
390→      const dir = path.dirname(filePath);
391→      fs.mkdirSync(dir, { recursive: true });
392→      fs.writeFileSync(filePath, text, "utf-8");
393→      return true;
394→    } catch (err) {
395→      log.error("[notes] Save failed:", err.message);
396→      return false;
397→    }
398→  });
399→
400→  // --- File attachments ---
401→
402→  ipcMain.handle("file:open-dialog", async () => {
403→    const result = await dialog.showOpenDialog({
404→      properties: ["openFile", "multiSelections"],
405→      filters: [
406→        {
407→          name: "Images & Documents",
408→          extensions: [
409→            "png", "jpg", "jpeg", "gif", "svg", "webp",
410→            "pdf", "doc", "docx", "txt", "md",
411→            "fig", "sketch", "xd", "ai", "psd",
412→          ],
413→        },
414→      ],
415→    });
416→    return { canceled: result.canceled, filePaths: result.filePaths };
417→  });
418→
419→  const MAX_FILE_SIZE = 50 * 1024 * 1024; // 50 MB
420→
421→  ipcMain.handle("file:copy-to-project", async (_event, { projectDir, filePaths }) => {
422→    const refsDir = path.join(projectDir, "references");
423→    fs.mkdirSync(refsDir, { recursive: true });
424→
425→    const results = [];
426→    for (const src of filePaths) {
427→      try {
428→        const stat = fs.statSync(src);
429→        if (stat.size > MAX_FILE_SIZE) {
430→          results.push({ src, dest: null, copied: false, error: "File exceeds 50 MB limit" });
431→          continue;
432→        }
433→
434→        const baseName = path.basename(src);
435→        const ext = path.extname(baseName);
436→        const nameNoExt = path.basename(baseName, ext);
437→
438→        let dest = path.join(refsDir, baseName);
439→        let counter = 1;
440→        while (fs.existsSync(dest)) {
441→          counter++;
442→          dest = path.join(refsDir, `${nameNoExt}-${counter}${ext}`);
443→        }
444→
445→        fs.copyFileSync(src, dest);
446→        results.push({ src, dest, copied: true });
447→      } catch (err) {
448→        results.push({ src, dest: null, copied: false, error: err.message });
449→      }
450→    }
451→
452→    log.info("[file] Copied", results.filter((r) => r.copied).length, "files to", refsDir);
453→    return results;
454→  });
455→
456→  log.info("IPC handlers registered");
457→}
458→
459→module.exports = { setupIPC };
460→

