 24→function ProjectStatusBadge({ project, t }: { project: ProjectMeta; t: (key: string) => string }) {
 25→  if (project.liveUrl) {
 26→    return (
 27→      <span className="flex items-center gap-1 text-xs font-bold text-emerald-600">
 28→        <Globe className="size-3" />
