 52→  const router = useRouter();
 53→  const store = useAppStore();
 54→
 55→  const [recentProjects, setRecentProjects] = useState<ProjectMeta[]>([]);
 56→  const [isCreating, setIsCreating] = useState(false);
 57→
 58→  const hasProjects = recentProjects.length > 0;
 59→
 60→  // Load recent projects on mount
 61→  useEffect(() => {
 62→    window.electronAPI
 63→      ?.listProjects?.()
 64→      .then((projects: ProjectMeta[]) => {
 65→        if (projects?.length) setRecentProjects(projects);
 66→      })
 67→      .catch(() => {});
 68→  }, []);
 69→
