 99→    } catch (err) {
100→      console.error("[welcome] Project switch failed:", err);
101→    }
102→  }
103→
104→  return (
105→    <div className="flex min-h-screen flex-col items-center justify-center bg-dummy-yellow p-8">
106→      {/* Mascot */}
107→      <motion.div
108→        initial={{ opacity: 0, scale: 0.9 }}
