  1"use client";
  2
  3import { useCallback, useEffect, useRef, useState } from "react";
  4import { useRouter } from "next/navigation";
  5import { useTranslations } from "next-intl";
  6import { motion } from "framer-motion";
  7import { Plus } from "lucide-react";
  8import { useAppStore } from "@/lib/store";
  9import { connectToAgent, type AgentCallbacks } from "@/lib/agent-client";
 10import { createMilestoneThrottler } from "@/lib/milestone-extractor";
 11import { useChatPersistence } from "@/lib/use-chat-persistence";
 12import { useDebugStore } from "@/lib/debug-store";
 13import { useDebugShortcut } from "@/lib/use-debug-shortcut";
 14import ChatPanel from "@/components/chat/ChatPanel";
 15import LivePreview from "@/components/preview/LivePreview";
 16import DebugTerminal from "@/components/debug/DebugTerminal";
 17import ProjectDrawer from "@/components/project/ProjectDrawer";
 18import ProjectDrawerToggle from "@/components/project/ProjectDrawerToggle";
 19
 20import StepIndicator from "@/components/progress/StepIndicator";
 21import Logo from "@/components/brand/Logo";
 22import LanguageToggle from "@/components/brand/LanguageToggle";
 23
 24function formatInitialMessage(state: {
 25  idea?: string | null;
 26  vibe?: string | null;
 27  audience?: string | null;
 28  priority?: string | null;
 29  designRef?: string | null;
 30  locale: "he" | "en";
 31}): string {
 32  const parts: string[] = [];
 33  if (state.locale === "he") {
 34    parts.push(state.idea ? `  : ${state.idea}` : "   ");
 35    if (state.vibe) parts.push(`: ${state.vibe}`);
 36    if (state.audience) parts.push(` : ${state.audience}`);
 37    if (state.priority) parts.push(`: ${state.priority}`);
 38    if (state.designRef) parts.push(`    /: ${state.designRef}`);
 39    else parts.push(`   !`);
 40  } else {
 41    parts.push(state.idea ? `I want to build: ${state.idea}` : "I want to build a website");
 42    if (state.vibe) parts.push(`Style: ${state.vibe}`);
 43    if (state.audience) parts.push(`Audience: ${state.audience}`);
 44    if (state.priority) parts.push(`Goal: ${state.priority}`);
 45    if (state.designRef) parts.push(`Here's a design I like: ${state.designRef}`);
 46    else parts.push(`Surprise me with the design!`);
 47  }
 48  return parts.join("\n");
 49}
