  6export interface ProjectMilestone {
  7  id: string;
  8  type: "choice" | "build" | "change" | "publish" | "other";
  9  label: string;
 10  timestamp: number;
 11}
 12
 13export interface ChatMessage {
 14  id: string;
 15  role: "user" | "assistant" | "status";
 16  content: string;
 17  timestamp: number;
 18  toolName?: string;
 19  toolInput?: { file_path?: string; command?: string };
 20  questionData?: {
 21    questions: Array<{
 22      question: string;
 23      options: Array<{ label: string; description: string }>;
 24    }>;
 25  };
 26}
