 28export interface ProjectMeta {
 29  id: string;
 30  name: string;
 31  displayName: string;
 32  path: string;
 33  createdAt: string;
 34  lastOpenedAt: string;
 35  sessionId: string | null;
 36  locale: string;
 37  vibe: string | null;
 38  audience: string | null;
 39  priority: string | null;
 40  designRef: string | null;
 41  liveUrl: string | null;
 42  githubUrl: string | null;
 43}
 44
 45interface AppState {
 46  // Session
 47  sessionId: string | null;
 48  projectDir: string | null;
 49  activeProjectId: string | null;
 50
 51  // i18n
 52  locale: "he" | "en";
 53
 54  // Progress
 55  currentStep: number;
 56  phase: number;
 57  completedSteps: number[];
 58
 59  // Chat
 60  messages: ChatMessage[];
 61  isStreaming: boolean;
 62  messagesLoaded: boolean;
 63  currentActivity: string | null;
 64
 65  // Project
 66  projectName: string | null;
 67  liveUrl: string | null;
 68  githubUrl: string | null;
 69
 70  // User
 71  userName: string | null;
 72  vibe: string | null;
 73  audience: string | null;
 74  priority: string | null;
 75  designRef: string | null;
 76  idea: string | null;
 77
 78  // Project drawer
 79  projectDrawerOpen: boolean;
 80  milestones: ProjectMilestone[];
 81
 82  // Runtime
 83  runtimeReady: boolean;
 84  claudeAuthenticated: boolean;
 85
 86  // Preview
 87  previewUrl: string | null;
 88  previewMode: "desktop" | "mobile";
 89
 90  // Actions
 91  setLocale: (locale: "he" | "en") => void;
 92  setSessionId: (id: string) => void;
 93  setProjectDir: (dir: string) => void;
 94  setStep: (step: number, phase: number) => void;
 95  completeStep: (step: number) => void;
 96  addMessage: (msg: ChatMessage) => void;
 97  loadMessages: (msgs: ChatMessage[]) => void;
 98  setStreaming: (streaming: boolean) => void;
 99  setCurrentActivity: (activity: string | null) => void;
100  setProjectName: (name: string) => void;
101  setLiveUrl: (url: string) => void;
102  setGithubUrl: (url: string) => void;
103  setUserName: (name: string) => void;
104  setIdea: (idea: string) => void;
105  setVibe: (vibe: string) => void;
106  setAudience: (audience: string) => void;
107  setPriority: (priority: string) => void;
108  setDesignRef: (ref: string | null) => void;
109  setRuntimeReady: (ready: boolean) => void;
110  setClaudeAuthenticated: (auth: boolean) => void;
111  setPreviewUrl: (url: string | null) => void;
112  setPreviewMode: (mode: "desktop" | "mobile") => void;
113  setActiveProjectId: (id: string | null) => void;
114  toggleProjectDrawer: () => void;
115  setProjectDrawerOpen: (open: boolean) => void;
116  addMilestone: (milestone: ProjectMilestone) => void;
117  loadMilestones: (milestones: ProjectMilestone[]) => void;
118  loadProject: (meta: ProjectMeta) => void;
119  reset: () => void;
120}
121
122const initialState = {
123  sessionId: null,
124  projectDir: null,
125  activeProjectId: null as string | null,
126  locale: "he" as const,
127  currentStep: 1,
128  phase: 0,
129  completedSteps: [],
130  messages: [],
131  isStreaming: false,
132  messagesLoaded: false,
133  currentActivity: null,
134  projectName: null,
135  liveUrl: null,
136  githubUrl: null,
137  userName: null,
138  vibe: null,
139  audience: null,
140  priority: null,
141  designRef: null,
142  idea: null,
143  projectDrawerOpen: false,
144  milestones: [] as ProjectMilestone[],
145  runtimeReady: false,
146  claudeAuthenticated: false,
147  previewUrl: null,
148  previewMode: "desktop" as const,
149};
150
151export const useAppStore = create<AppState>()(
152  persist(
153    (set) => ({
154      ...initialState,
155
156      setLocale: (locale) => set({ locale }),
157      setSessionId: (sessionId) => set({ sessionId }),
158      setProjectDir: (projectDir) => set({ projectDir }),
159      setStep: (currentStep, phase) => set({ currentStep, phase }),
160      completeStep: (step) =>
161        set((state) => ({
162          completedSteps: [...new Set([...state.completedSteps, step])],
163        })),
164      addMessage: (msg) =>
165        set((state) => ({ messages: [...state.messages, msg] })),
166      loadMessages: (msgs) => set({ messages: msgs, messagesLoaded: true }),
167      setStreaming: (isStreaming) => set({ isStreaming }),
168      setCurrentActivity: (currentActivity) => set({ currentActivity }),
169      setProjectName: (projectName) => set({ projectName }),
170      setLiveUrl: (liveUrl) => set({ liveUrl }),
171      setGithubUrl: (githubUrl) => set({ githubUrl }),
172      setUserName: (userName) => set({ userName }),
173      setIdea: (idea) => set({ idea }),
174      setVibe: (vibe) => set({ vibe }),
175      setAudience: (audience) => set({ audience }),
176      setPriority: (priority) => set({ priority }),
177      setDesignRef: (designRef) => set({ designRef }),
178      setRuntimeReady: (runtimeReady) => set({ runtimeReady }),
179      setClaudeAuthenticated: (claudeAuthenticated) =>
180        set({ claudeAuthenticated }),
181      setPreviewUrl: (previewUrl) => set({ previewUrl }),
182      setPreviewMode: (previewMode) => set({ previewMode }),
183      setActiveProjectId: (activeProjectId) => set({ activeProjectId }),
184      toggleProjectDrawer: () =>
185        set((state) => ({ projectDrawerOpen: !state.projectDrawerOpen })),
186      setProjectDrawerOpen: (projectDrawerOpen) => set({ projectDrawerOpen }),
187      addMilestone: (milestone) =>
188        set((state) => ({ milestones: [...state.milestones, milestone] })),
189      loadMilestones: (milestones) => set({ milestones }),
190      loadProject: (meta) =>
191        set({
192          activeProjectId: meta.id,
193          projectDir: meta.path,
194          projectName: meta.name,
195          idea: meta.displayName,
196          vibe: meta.vibe,
197          audience: meta.audience,
198          priority: meta.priority,
199          designRef: meta.designRef,
200          sessionId: meta.sessionId,
201          liveUrl: meta.liveUrl,
202          githubUrl: meta.githubUrl,
203          // Reset transient state  messages loaded by persistence hook
204          isStreaming: false,
205          messagesLoaded: false,
206          currentActivity: null,
207          previewUrl: null,
208          previewMode: "desktop",
209          projectDrawerOpen: false,
210          milestones: [],
211        }),
212      reset: () => set({ ...initialState }),
213    }),
214    {
215      name: "cc4d-desktop-store",
216      partialize: (state) => ({
217        locale: state.locale,
218        sessionId: state.sessionId,
219        projectDir: state.projectDir,
220        activeProjectId: state.activeProjectId,
221        currentStep: state.currentStep,
222        phase: state.phase,
223        completedSteps: state.completedSteps,
224        projectName: state.projectName,
225        liveUrl: state.liveUrl,
226        githubUrl: state.githubUrl,
227        userName: state.userName,
228        vibe: state.vibe,
229        idea: state.idea,
230      }),
231    }
232  )
233);
