 1"use client";
 2
 3import { useRef, useEffect, useMemo } from "react";
 4import { useAppStore } from "@/lib/store";
 5import { buildActivityBlocks } from "@/lib/activity-blocks";
 6import ActivityBlockView from "./ActivityBlockView";
 7
 8interface ChatHistoryProps {
 9  onAnswer: (answer: string) => void;
10}
11
12export default function ChatHistory({ onAnswer }: ChatHistoryProps) {
13  const messages = useAppStore((s) => s.messages);
14  const isStreaming = useAppStore((s) => s.isStreaming);
15  const messagesEndRef = useRef<HTMLDivElement>(null);
16
17  const blocks = useMemo(() => {
18    const built = buildActivityBlocks(messages);
19    // Mark last block as active if we're streaming
20    if (isStreaming && built.length > 0) {
21      built[built.length - 1] = {
22        ...built[built.length - 1],
23        isActive: true,
24      };
25    }
26    return built;
27  }, [messages, isStreaming]);
28
29  useEffect(() => {
30    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
31  }, [messages, isStreaming]);
32
33  return (
34    <div className="flex-1 overflow-y-auto p-4 space-y-3">
35      {blocks.map((block) => (
36        <ActivityBlockView key={block.id} block={block} onAnswer={onAnswer} />
37      ))}
38      <div ref={messagesEndRef} />
39    </div>
40  );
41}
