24        <span className="text-sm text-dummy-black/60">{message.content}</span>
25      </motion.div>
26    );
27  }
28
29  return (
30    <motion.div
31      initial={{ opacity: 0, y: 8 }}
32      animate={{ opacity: 1, y: 0 }}
33      className={`flex gap-3 ${isUser ? "flex-row-reverse" : ""}`}
34    >
35      {/* Avatar */}
36      {!isUser && (
37        <div className="shrink-0">
38          <MascotImage
39            pose="peeking"
40            alt=""
41            width={36}
42            height={36}
43            className="rounded-full"
44          />
45        </div>
46      )}
47
48      {/* Bubble */}
49      <div
50        className={`max-w-[80%] rounded-2xl px-4 py-3 ${
51          isUser
52            ? "bg-dummy-black text-dummy-yellow"
53            : "bg-dummy-white text-dummy-black shadow-sm border-2 border-dummy-black/10"
54        }`}
55      >
56        <p className="whitespace-pre-wrap text-sm leading-relaxed">
57          {message.content}
58        </p>
59      </div>
60    </motion.div>
61  );
62}
