  1import { NextRequest } from "next/server";
  2import { spawn } from "child_process";
  3import { existsSync } from "fs";
  4import { join } from "path";
  5import { homedir } from "os";
  6import { agentLog, createLogger } from "@/lib/logger";
  7
  8const debugLog = createLogger("agent-debug");
  9
 10export const runtime = "nodejs";
 11export const dynamic = "force-dynamic";
 12
 13function findClaude(): string {
 14  const candidates = [
 15    join(homedir(), ".local", "bin", "claude"),
 16    join(homedir(), ".claude", "bin", "claude"),
 17    "/usr/local/bin/claude",
 18    "/opt/homebrew/bin/claude",
 19  ];
 20  for (const p of candidates) {
 21    if (existsSync(p)) return p;
 22  }
 23  return "claude";
 24}
 25
 26const claudePath = findClaude();
 27agentLog.info("Claude binary resolved", { path: claudePath });
 28
 29/**
 30 * SSE endpoint that streams Claude CLI messages to the renderer.
 31 * Spawns `claude --print --output-format stream-json` as a subprocess.
 32 *
 33 * Key: stdin must be "ignore"  claude CLI hangs if stdin is piped.
 34 */
 35export async function POST(req: NextRequest) {
 36  const { prompt, locale, projectDir, sessionId } = await req.json();
 37
 38  const requestId = `req-${Date.now().toString(36)}`;
 39  agentLog.info(`[${requestId}] New agent request`, {
 40    locale,
 41    sessionId: sessionId || null,
 42    promptLength: prompt.length,
 43    cwd: projectDir || process.cwd(),
 44  });
 45
 46  const encoder = new TextEncoder();
 47
 48  const localizedPrompt =
 49    locale === "he"
 50      ? `[IMPORTANT: Respond in Hebrew. The user speaks Hebrew.]\n\n${prompt}`
 51      : prompt;
 52
 53  const args = [
 54    "--print",
 55    "--output-format",
 56    "stream-json",
 57    "--dangerously-skip-permissions",
 58    "--verbose",
 59  ];
 60
 61  if (sessionId) {
 62    args.push("--resume", sessionId);
 63  }
 64
 65  args.push(localizedPrompt);
 66
 67  const env = { ...process.env };
 68  delete env.CLAUDECODE;
 69
 70  const stream = new ReadableStream({
 71    start(controller) {
 72      agentLog.info(`[${requestId}] Spawning claude`, {
 73        binary: claudePath,
 74        args: args.slice(0, -1), // exclude prompt
 75      });
 76
 77      const child = spawn(claudePath, args, {
 78        cwd: projectDir || process.cwd(),
 79        env,
 80        stdio: ["ignore", "pipe", "pipe"],
 81      });
 82
 83      const childPid = child.pid;
 84      agentLog.info(`[${requestId}] Claude process started`, { pid: childPid });
 85
 86      let buffer = "";
 87      let messageCount = 0;
 88
 89      child.stdout!.on("data", (data: Buffer) => {
 90        buffer += data.toString();
 91        const lines = buffer.split("\n");
 92        buffer = lines.pop() || "";
 93
 94        for (const line of lines) {
 95          if (!line.trim()) continue;
 96          try {
 97            const parsed = JSON.parse(line);
 98            messageCount++;
 99            debugLog.debug(`[${requestId}] raw`, parsed);
100
101            // Log message type and key info (not full payload  too large)
102            const logEntry: Record<string, unknown> = {
103              type: parsed.type,
104              seq: messageCount,
105            };
106            if (parsed.type === "assistant" && parsed.message?.content) {
107              const content = parsed.message.content;
108              if (Array.isArray(content)) {
109                logEntry.blocks = content.map((b: Record<string, unknown>) => {
110                  if (b.type === "text") return { type: "text", length: (b.text as string)?.length };
111                  if (b.type === "tool_use") return { type: "tool_use", name: b.name };
112                  return { type: b.type };
113                });
114              }
115            }
116            if (parsed.type === "result") {
117              logEntry.subtype = parsed.subtype;
118              logEntry.duration_ms = parsed.duration_ms;
119              logEntry.num_turns = parsed.num_turns;
120              logEntry.cost_usd = parsed.total_cost_usd;
121              logEntry.session_id = parsed.session_id;
122            }
123            agentLog.debug(`[${requestId}] SSE event`, logEntry);
124
125            controller.enqueue(encoder.encode(`data: ${line}\n\n`));
126          } catch {
127            agentLog.warn(`[${requestId}] Non-JSON stdout line`, {
128              preview: line.slice(0, 200),
129            });
130          }
131        }
132      });
133
134      child.stderr!.on("data", (data: Buffer) => {
135        const text = data.toString().trim();
136        if (!text) return;
137        agentLog.warn(`[${requestId}] Claude stderr`, { text: text.slice(0, 500) });
138        if (text.includes("Error") || text.includes("error")) {
139          controller.enqueue(
140            encoder.encode(
141              `data: ${JSON.stringify({ type: "error", content: text })}\n\n`
142            )
143          );
144        }
145      });
146
147      child.on("error", (err) => {
148        agentLog.error(`[${requestId}] Spawn error`, { error: err.message });
149        controller.enqueue(
150          encoder.encode(
151            `data: ${JSON.stringify({ type: "error", content: err.message })}\n\n`
152          )
153        );
154        controller.enqueue(encoder.encode("data: [DONE]\n\n"));
155        controller.close();
156      });
157
158      child.on("close", (code, signal) => {
159        agentLog.info(`[${requestId}] Claude process exited`, {
160          pid: childPid,
161          code,
162          signal,
163          messageCount,
164        });
165        if (buffer.trim()) {
166          try {
167            JSON.parse(buffer);
168            controller.enqueue(encoder.encode(`data: ${buffer}\n\n`));
169          } catch {
170            // ignore
171          }
172        }
173        controller.enqueue(encoder.encode("data: [DONE]\n\n"));
174        controller.close();
175      });
176
177      req.signal.addEventListener("abort", () => {
178        agentLog.info(`[${requestId}] Client disconnected, killing claude`, {
179          pid: childPid,
180        });
181        child.kill("SIGTERM");
182      });
183    },
184  });
185
186  return new Response(stream, {
187    headers: {
188      "Content-Type": "text/event-stream",
189      "Cache-Control": "no-cache, no-transform",
190      Connection: "keep-alive",
191      "X-Accel-Buffering": "no",
192    },
193  });
194}
