 6import BuildingBlock from "./BuildingBlock";
 7import QuestionCard from "./QuestionCard";
 8import ErrorMessage from "./ErrorMessage";
 9
10interface ActivityBlockViewProps {
11  block: ActivityBlock;
12  onAnswer: (answer: string) => void;
13}
14
15export default function ActivityBlockView({
16  block,
17  onAnswer,
18}: ActivityBlockViewProps) {
19  switch (block.type) {
20    case "user-input":
21      return (
22        <>
23          {block.messages.map((msg) => (
24            <UserMessage key={msg.id} content={msg.content} />
25          ))}
26        </>
27      );
28
29    case "assistant":
30      return (
31        <>
32          {block.messages.map((msg) => (
33            <AssistantMessage key={msg.id} content={msg.content} />
34          ))}
35        </>
36      );
37
38    case "building":
39      return <BuildingBlock block={block} />;
40
41    case "question":
42      return (
43        <>
44          {block.messages.map((msg) =>
45            msg.questionData ? (
46              <QuestionCard key={msg.id} message={msg} onAnswer={onAnswer} />
47            ) : null
48          )}
49        </>
50      );
51
52    case "error":
53      return (
54        <>
55          {block.messages.map((msg) => (
56            <ErrorMessage key={msg.id} content={msg.content} />
57          ))}
58        </>
59      );
60
61    default:
62      return null;
63  }
64}
