 83 * - {"type":"system","subtype":"init",...}  session init (skip)
 84 * - {"type":"assistant","message":{"content":[{"type":"text","text":"..."}]}}  assistant text
 85 * - {"type":"assistant","message":{"content":[{"type":"tool_use","name":"Bash","input":{...}}]}}  tool use
 86 * - {"type":"user","message":{"content":[{"type":"tool_result",...}]}}  tool result (skip)
 87 * - {"type":"result","subtype":"success","result":"..."}  final result
 88 * - {"type":"error",...}  error
 89 */
 90export function parseAgentEvent(
 91  raw: Record<string, unknown>,
 92  locale: "he" | "en",
 93  callbacks?: AgentCallbacks
 94): ChatMessage | null {
 95  const type = raw.type as string;
 96  const now = Date.now();
 97
 98  // Skip system/init, rate_limit, user (tool_result) messages
 99  if (type === "system" || type === "rate_limit_event" || type === "user") {
100    return null;
101  }
102
103  // Assistant message  contains content blocks
104  if (type === "assistant") {
105    const message = raw.message as Record<string, unknown> | undefined;
106    if (!message) return null;
107
108    const content = message.content as
109      | Array<Record<string, unknown>>
110      | undefined;
111    if (!Array.isArray(content)) return null;
112
113    // Process each content block
114    for (const block of content) {
115      const blockType = block.type as string;
116
117      // Text block  show as assistant message + detect live URLs
118      if (blockType === "text") {
119        const text = (block.text as string) || "";
120        if (!text.trim()) continue;
121
122        // Detect Vercel deployment URL
123        const urlMatch = text.match(/https:\/\/[\w-]+\.vercel\.app/);
124        if (urlMatch && callbacks?.onLiveUrl) {
125          callbacks.onLiveUrl(urlMatch[0]);
126        }
127
128        return {
129          id: `msg-${uid()}`,
130          role: "assistant",
131          content: text,
132          timestamp: now,
133        };
134      }
135
136      // Tool use block  translate to friendly status
137      if (blockType === "tool_use") {
138        const toolName = block.name as string;
139        const toolInput = block.input as
140          | Record<string, unknown>
141          | undefined;
142
143        // Bash commands  friendly message + detect dev server / progress
144        if (toolName === "Bash" && toolInput?.command) {
145          const command = toolInput.command as string;
146
147          // Detect dev server start
148          if (command.includes("npm run dev") || command.includes("next dev")) {
149            callbacks?.onDevServerDetected?.();
150          }
151
152          // Detect progress completion
153          const progressMatch = command.match(/progress\.sh\s+complete\s+(\d+)/);
154          if (progressMatch) {
155            callbacks?.onStepCompleted?.(parseInt(progressMatch[1], 10));
156          }
157
158          const friendly = matchToolMessage(command, locale);
159          const msg: ChatMessage = {
160            id: `status-${uid()}`,
161            role: "status",
162            content: friendly || (locale === "he" ? "..." : "Building..."),
163            timestamp: now,
164            toolName,
165            toolInput: { command },
166          };
167          useAppStore.getState().setCurrentActivity(msg.content);
168          return msg;
169        }
170
171        // Write  show filename + notify file change
172        if (toolName === "Write" && toolInput?.file_path) {
173          callbacks?.onFileChanged?.(toolInput.file_path as string);
174          const fileName = basename(toolInput.file_path as string);
175          const content =
176            locale === "he"
177              ? `...${fileName} `
178              : `Creating ${fileName}...`;
179          const msg: ChatMessage = {
180            id: `status-${uid()}`,
181            role: "status",
182            content,
183            timestamp: now,
184            toolName,
185            toolInput: { file_path: toolInput.file_path as string },
186          };
187          useAppStore.getState().setCurrentActivity(msg.content);
188          return msg;
189        }
190
191        // Edit  show filename + notify file change
192        if (toolName === "Edit" && toolInput?.file_path) {
193          callbacks?.onFileChanged?.(toolInput.file_path as string);
194          const fileName = basename(toolInput.file_path as string);
195          const content =
196            locale === "he"
197              ? `...${fileName} `
198              : `Editing ${fileName}...`;
199          const msg: ChatMessage = {
200            id: `status-${uid()}`,
201            role: "status",
202            content,
203            timestamp: now,
204            toolName,
205            toolInput: { file_path: toolInput.file_path as string },
206          };
207          useAppStore.getState().setCurrentActivity(msg.content);
208          return msg;
209        }
210
211        // Write/Edit without file_path (fallback)
212        if (toolName === "Write" || toolName === "Edit") {
213          const msg: ChatMessage = {
214            id: `status-${uid()}`,
215            role: "status",
216            content:
217              locale === "he" ? "... " : "Making changes...",
218            timestamp: now,
219            toolName,
220          };
221          useAppStore.getState().setCurrentActivity(msg.content);
222          return msg;
223        }
224
225        // Glob/Grep/Read  skip (internal, not interesting to user)
226        if (
227          toolName === "Glob" ||
228          toolName === "Grep" ||
229          toolName === "Read"
230        ) {
231          return null;
232        }
233
234        // AskUserQuestion  pass through as question
235        if (toolName === "AskUserQuestion") {
236          return {
237            id: `question-${uid()}`,
238            role: "assistant",
239            content: "",
240            timestamp: now,
241            toolName,
242            questionData: toolInput as ChatMessage["questionData"],
243          };
244        }
245
246        // Other tools  generic status
247        return null;
248      }
249    }
250
251    return null;
252  }
253
254  // Final result
255  if (type === "result") {
256    const result = raw.result as string | undefined;
257    const subtype = raw.subtype as string;
258
259    useAppStore.getState().setCurrentActivity(null);
260
261    if (subtype === "error" || raw.is_error) {
262      return {
263        id: `error-${uid()}`,
264        role: "status",
265        content:
266          locale === "he"
267            ? "...,  "
268            : "Hmm, let me try that again...",
269        timestamp: now,
270      };
271    }
272
273    // Success result  skip, the content was already streamed via assistant text blocks.
274    // Emitting it again would cause duplicate messages.
275    return null;
276  }
277
278  // Error
279  if (type === "error") {
280    useAppStore.getState().setCurrentActivity(null);
281    return {
282      id: `error-${uid()}`,
283      role: "status",
284      content:
285        locale === "he"
286          ? "...,  "
287          : "Hmm, let me try that again...",
288      timestamp: now,
289    };
290  }
291
292  return null;
293}
