295/**
296 * Connect to the agent SSE stream.
297 */
298export function connectToAgent(options: {
299  prompt: string;
300  locale: "he" | "en";
301  projectDir?: string;
302  sessionId?: string;
303  onMessage: (msg: ChatMessage) => void;
304  onDone: () => void;
305  onError: (err: string) => void;
306  onSessionId?: (sessionId: string) => void;
307  onRawEvent?: (raw: Record<string, unknown>, timestamp: number) => void;
308  callbacks?: AgentCallbacks;
309}): { abort: () => void } {
310  const controller = new AbortController();
311
312  const body = JSON.stringify({
313    prompt: options.prompt,
314    locale: options.locale,
315    projectDir: options.projectDir,
316    sessionId: options.sessionId,
317  });
318
319  fetch("/api/agent", {
320    method: "POST",
321    headers: { "Content-Type": "application/json" },
322    body,
323    signal: controller.signal,
324  })
325    .then(async (res) => {
326      if (!res.ok || !res.body) {
327        options.onError(`HTTP ${res.status}`);
328        return;
329      }
330
331      const reader = res.body.getReader();
332      const decoder = new TextDecoder();
333      let buffer = "";
334
335      while (true) {
336        const { done, value } = await reader.read();
337        if (done) break;
338
339        buffer += decoder.decode(value, { stream: true });
340        const lines = buffer.split("\n");
341        buffer = lines.pop() || "";
342
343        for (const line of lines) {
344          if (line.startsWith("data: ")) {
345            const data = line.slice(6);
346            if (data === "[DONE]") {
347              useAppStore.getState().setCurrentActivity(null);
348              options.onDone();
349              return;
350            }
351            try {
352              const parsed = JSON.parse(data);
353              options.onRawEvent?.(parsed, Date.now());
354              // Capture session_id from result events
355              if (parsed.session_id && options.onSessionId) {
356                options.onSessionId(parsed.session_id as string);
357              }
358              const msg = parseAgentEvent(parsed, options.locale, options.callbacks);
359              if (msg) options.onMessage(msg);
360            } catch {
361              // Skip unparseable lines
362            }
363          }
364        }
365      }
366
367      useAppStore.getState().setCurrentActivity(null);
368      options.onDone();
369    })
370    .catch((err) => {
371      if (err.name !== "AbortError") {
372        useAppStore.getState().setCurrentActivity(null);
373        options.onError(err.message);
374      }
375    });
376
377  return {
378    abort: () => controller.abort(),
379  };
380}
