  1import type { ChatMessage, ProjectMilestone } from "./store";
  2
  3function uid(): string {
  4  return `ms-${Date.now()}-${Math.random().toString(36).slice(2, 6)}`;
  5}
  6
  7/**
  8 * Examine an incoming ChatMessage and extract a milestone if it represents
  9 * a noteworthy event (choice, build action, publish, etc.).
 10 * Returns null when the message isn't milestone-worthy.
 11 */
 12export function extractMilestone(
 13  msg: ChatMessage,
 14  locale: "he" | "en"
 15): ProjectMilestone | null {
 16  const now = msg.timestamp || Date.now();
 17
 18  // Tool-based milestones
 19  if (msg.toolName) {
 20    const command = msg.toolInput?.command || "";
 21
 22    // Scaffolding / creating project
 23    if (command.includes("create-next-app") || command.includes("npm init")) {
 24      return {
 25        id: uid(),
 26        type: "build",
 27        label: locale === "he" ? " " : "Project created",
 28        timestamp: now,
 29      };
 30    }
 31
 32    // Dev server started
 33    if (command.includes("npm run dev") || command.includes("next dev")) {
 34      return {
 35        id: uid(),
 36        type: "build",
 37        label: locale === "he" ? "  " : "First version built",
 38        timestamp: now,
 39      };
 40    }
 41
 42    // Publishing
 43    if (command.includes("vercel") || command.includes("deploy")) {
 44      return {
 45        id: uid(),
 46        type: "publish",
 47        label: locale === "he" ? " " : "Site published",
 48        timestamp: now,
 49      };
 50    }
 51
 52    // File writes/edits  aggregate as "Made changes"
 53    if (msg.toolName === "Write" || msg.toolName === "Edit") {
 54      const fileName = msg.toolInput?.file_path?.split("/").pop() || "";
 55      return {
 56        id: uid(),
 57        type: "change",
 58        label:
 59          locale === "he"
 60            ? `: ${fileName}`
 61            : `Edited: ${fileName}`,
 62        timestamp: now,
 63      };
 64    }
 65
 66    return null;
 67  }
 68
 69  // Assistant text milestones  look for key phrases
 70  if (msg.role === "assistant" && msg.content) {
 71    const text = msg.content.toLowerCase();
 72
 73    // Detect live URL announcement
 74    if (text.includes("vercel.app") || text.includes("your site is live")) {
 75      return {
 76        id: uid(),
 77        type: "publish",
 78        label: locale === "he" ? " !" : "Site is live!",
 79        timestamp: now,
 80      };
 81    }
 82
 83    return null;
 84  }
 85
 86  return null;
 87}
 88
 89/**
 90 * Throttle file-change milestones so we don't flood the log with
 91 * "Edited: foo.tsx" for every single file. Groups edits within a
 92 * 10-second window into a single "Made changes" entry.
 93 */
 94export function createMilestoneThrottler(locale: "he" | "en") {
 95  let lastFileChangeMilestone = 0;
 96  const FILE_CHANGE_COOLDOWN = 10_000; // 10 seconds
 97
 98  return function maybeExtract(msg: ChatMessage): ProjectMilestone | null {
 99    const milestone = extractMilestone(msg, locale);
100    if (!milestone) return null;
101
102    // Throttle file changes
103    if (milestone.type === "change") {
104      const now = Date.now();
105      if (now - lastFileChangeMilestone < FILE_CHANGE_COOLDOWN) {
106        return null;
107      }
108      lastFileChangeMilestone = now;
109      // Generalize throttled edits
110      milestone.label = locale === "he" ? " " : "Made changes";
111    }
112
113    return milestone;
114  };
115}
