 51export default function BuildPage() {
 52  const t = useTranslations("Build");
 53  const router = useRouter();
 54  const store = useAppStore();
 55  const abortRef = useRef<{ abort: () => void } | null>(null);
 56  const hasStarted = useRef(false);
 57  const milestoneExtractor = useRef(createMilestoneThrottler(store.locale));
 58  const [showRecoveryBanner, setShowRecoveryBanner] = useState(false);
 59  const [refreshTrigger, setRefreshTrigger] = useState(0);
 60
 61  useDebugShortcut();
 62
 63  // Persistence
 64  const { isLoaded } = useChatPersistence(store.projectDir);
 65
 66  // Recover projectDir from electron-store if missing
 67  useEffect(() => {
 68    if (!store.projectDir) {
 69      window.electronAPI?.getActiveProject?.().then((meta: { path?: string; id?: string; name?: string } | null) => {
 70        if (meta?.path) {
 71          store.setProjectDir(meta.path);
 72          if (meta.id) store.setActiveProjectId(meta.id);
 73          if (meta.name) store.setProjectName(meta.name);
 74        }
 75      }).catch(() => {});
 76    }
 77  }, []); // eslint-disable-line react-hooks/exhaustive-deps
 78
 79  // Load milestones when project dir is available
 80  useEffect(() => {
 81    if (!store.projectDir) return;
 82    window.electronAPI?.loadMilestones?.(store.projectDir).then((milestones: unknown[]) => {
 83      if (milestones?.length) {
 84        store.loadMilestones(milestones as Parameters<typeof store.loadMilestones>[0]);
 85      }
 86    }).catch(() => {});
 87  }, [store.projectDir]); // eslint-disable-line react-hooks/exhaustive-deps
 88
 89  // Start agentic flow once persistence has loaded
 90  useEffect(() => {
 91    if (hasStarted.current) return;
 92    if (!isLoaded) return;
 93    if (!store.projectDir) return;
 94    hasStarted.current = true;
 95
 96    // If we have restored messages, show recovery banner and skip initial prompt
 97    if (store.messages.length > 0) {
 98      setShowRecoveryBanner(true);
 99      return;
100    }
101
102    // If resuming a session, don't re-send the initial prompt
103    if (store.sessionId) return;
104
105    sendToAgent(formatInitialMessage({
106      idea: store.idea,
107      vibe: store.vibe,
108      audience: store.audience,
109      priority: store.priority,
110      designRef: store.designRef,
111      locale: store.locale,
112    }));
113  }, [isLoaded, store.projectDir]); // eslint-disable-line react-hooks/exhaustive-deps
114
115  const sendToAgent = useCallback(
116    (prompt: string) => {
117      // Add user message to chat
118      store.addMessage({
119        id: `user-${Date.now()}`,
120        role: "user",
121        content: prompt,
122        timestamp: Date.now(),
123      });
124
125      store.setStreaming(true);
126
127      abortRef.current = connectToAgent({
128        prompt,
129        locale: store.locale,
130        projectDir: store.projectDir || undefined,
131        sessionId: store.sessionId || undefined,
132        onMessage: (msg) => {
133          store.addMessage(msg);
134          // Extract milestones for the project activity log
135          const milestone = milestoneExtractor.current(msg);
136          if (milestone) {
137            store.addMilestone(milestone);
138            // Persist milestones
139            if (store.projectDir) {
140              const allMilestones = [...useAppStore.getState().milestones];
141              window.electronAPI?.saveMilestones?.(store.projectDir, allMilestones).catch(() => {});
142            }
143          }
144        },
145        onSessionId: (sessionId) => {
146          store.setSessionId(sessionId);
147          // Persist to electron-store
148          const projectId = store.activeProjectId;
149          if (projectId) {
150            window.electronAPI?.updateProject?.(projectId, { sessionId }).catch(() => {});
151          }
152        },
153        onDone: () => {
154          store.setStreaming(false);
155        },
156        onRawEvent: (raw, timestamp) => {
157          useDebugStore.getState().addEvent(raw, timestamp);
158        },
159        onError: (err) => {
160          store.setStreaming(false);
161          store.addMessage({
162            id: `error-${Date.now()}`,
163            role: "status",
164            content:
165              store.locale === "he"
166                ? "...,  "
167                : "Hmm, let me try that again...",
168            timestamp: Date.now(),
169          });
170        },
171        callbacks: {
172          onDevServerDetected: () => {
173            // Poll port 3000 and set preview URL when ready
174            window.electronAPI?.pollPort?.(3000).then((ready: boolean) => {
175              if (ready) {
176                store.setPreviewUrl("http://localhost:3000");
177              }
178            });
179          },
180          onFileChanged: () => {
181            setRefreshTrigger((c) => c + 1);
182          },
183          onStepCompleted: (step) => {
184            store.completeStep(step);
185            const phase = step <= 2 ? 0 : step <= 4 ? 1 : step <= 6 ? 2 : 3;
186            store.setStep(step + 1, phase);
187          },
188          onLiveUrl: (url) => {
189            store.setLiveUrl(url);
190            const projectId = store.activeProjectId;
191            if (projectId) {
192              window.electronAPI?.updateProject?.(projectId, { liveUrl: url }).catch(() => {});
193            }
194          },
195        } satisfies AgentCallbacks,
196      });
197    },
198    [store]
199  );
200
201  function handleSend(message: string) {
202    if (abortRef.current) {
203      abortRef.current.abort();
204    }
205    sendToAgent(message);
206  }
207
208  return (
209    <div className="flex h-screen flex-col bg-dummy-yellow">
210      {/* Header */}
211      <header className="drag-region flex items-center justify-between border-b-2 border-dummy-black/10 bg-dummy-yellow px-4 py-2" style={{ paddingLeft: 80 }}>
212        <div className="no-drag flex items-center gap-2">
213          <ProjectDrawerToggle />
214          <Logo />
215        </div>
216        <StepIndicator />
217        <div className="no-drag flex items-center gap-2">
218          <button
219            onClick={() => {
220              if (store.isStreaming) {
221                store.setProjectDrawerOpen(true);
222              } else {
223                store.reset();
224                router.push("/welcome");
225              }
226            }}
227            className="rounded-md border-2 border-dummy-black/20 bg-dummy-white p-1.5 text-dummy-black/60 transition-colors hover:border-dummy-black hover:bg-dummy-black hover:text-dummy-yellow"
228            aria-label="New project"
229          >
230            <Plus className="size-4" />
231          </button>
232          <LanguageToggle />
233        </div>
234      </header>
235
236      {/* Main content: chat + optional preview */}
237      <div className="flex flex-1 overflow-hidden">
238        {/* Chat panel */}
239        <div className={`flex flex-col overflow-hidden bg-dummy-yellow-bright/30 ${store.previewUrl ? "w-1/2" : "w-full"}`}>
240          <ChatPanel onSend={handleSend} showRecoveryBanner={showRecoveryBanner} />
241        </div>
242
243        {/* Live preview (shown when dev server is running) */}
244        {store.previewUrl && (
245          <div className="w-1/2 border-s-2 border-dummy-black/10">
246            <LivePreview refreshTrigger={refreshTrigger} />
247          </div>
248        )}
249      </div>
250
251      {/* Feedback banner */}
252      {store.completedSteps.includes(4) && !store.isStreaming && (
253        <motion.div
254          initial={{ opacity: 0, y: 20 }}
255          animate={{ opacity: 1, y: 0 }}
256          className="border-t-2 border-dummy-black/10 bg-dummy-white px-4 py-3 text-center"
257        >
258          <p className="text-sm font-medium text-dummy-black">
259            {t("feedbackBanner")}
260          </p>
261        </motion.div>
262      )}
263
264      <DebugTerminal />
265      <ProjectDrawer />
266    </div>
267  );
268}
