 12function relativeTime(
 13  dateStr: string,
 14  // eslint-disable-next-line @typescript-eslint/no-explicit-any
 15  t: any
 16): string {
 17  const diff = Date.now() - new Date(dateStr).getTime();
 18  const days = Math.floor(diff / (1000 * 60 * 60 * 24));
 19  if (days === 0) return t("justNow");
 20  if (days === 1) return t("yesterday");
 21  return t("daysAgo", { count: days });
 22}
