  1import type { ChatMessage } from "./store";
  2
  3export interface ActivityBlock {
  4  id: string;
  5  type: "user-input" | "assistant" | "building" | "question" | "error";
  6  messages: ChatMessage[];
  7  isActive: boolean;
  8  filesChanged?: string[];
  9}
 10
 11function extractFilePath(msg: ChatMessage): string | undefined {
 12  return msg.toolInput?.file_path;
 13}
 14
 15function getBlockType(
 16  msg: ChatMessage
 17): ActivityBlock["type"] {
 18  if (msg.role === "user") return "user-input";
 19  if (msg.questionData) return "question";
 20  if (msg.id.startsWith("error-")) return "error";
 21  if (msg.role === "status") return "building";
 22  return "assistant";
 23}
 24
 25function createBlock(msg: ChatMessage, index: number): ActivityBlock {
 26  const type = getBlockType(msg);
 27  const filePath = extractFilePath(msg);
 28  return {
 29    id: `block-${index}`,
 30    type,
 31    messages: [msg],
 32    isActive: false,
 33    filesChanged: filePath ? [filePath] : [],
 34  };
 35}
 36
 37/**
 38 * Full rebuild of activity blocks from a messages array.
 39 */
 40export function buildActivityBlocks(messages: ChatMessage[]): ActivityBlock[] {
 41  const blocks: ActivityBlock[] = [];
 42
 43  for (let i = 0; i < messages.length; i++) {
 44    const msg = messages[i];
 45    const type = getBlockType(msg);
 46    const last = blocks[blocks.length - 1];
 47
 48    // Try to merge into current block
 49    if (last && last.type === type && type === "building") {
 50      last.messages.push(msg);
 51      const fp = extractFilePath(msg);
 52      if (fp && last.filesChanged && !last.filesChanged.includes(fp)) {
 53        last.filesChanged.push(fp);
 54      }
 55      continue;
 56    }
 57
 58    if (last && last.type === type && type === "assistant") {
 59      last.messages.push(msg);
 60      continue;
 61    }
 62
 63    // New block
 64    blocks.push(createBlock(msg, i));
 65  }
 66
 67  return blocks;
 68}
 69
 70/**
 71 * Incrementally append a message to existing blocks.
 72 * Returns a new blocks array (immutable).
 73 */
 74export function appendToBlocks(
 75  blocks: ActivityBlock[],
 76  msg: ChatMessage
 77): ActivityBlock[] {
 78  const type = getBlockType(msg);
 79  const last = blocks[blocks.length - 1];
 80
 81  // Deactivate previous active block if we're starting a new type
 82  const deactivated = blocks.map((b) =>
 83    b.isActive ? { ...b, isActive: false } : b
 84  );
 85
 86  // Try to merge
 87  if (last && last.type === type && (type === "building" || type === "assistant")) {
 88    const updated = [...deactivated];
 89    const lastBlock = { ...updated[updated.length - 1] };
 90    lastBlock.messages = [...lastBlock.messages, msg];
 91    lastBlock.isActive = true;
 92    if (type === "building") {
 93      const fp = extractFilePath(msg);
 94      if (fp && lastBlock.filesChanged && !lastBlock.filesChanged.includes(fp)) {
 95        lastBlock.filesChanged = [...lastBlock.filesChanged, fp];
 96      }
 97    }
 98    updated[updated.length - 1] = lastBlock;
 99    return updated;
100  }
101
102  // New block
103  const filePath = extractFilePath(msg);
104  return [
105    ...deactivated,
106    {
107      id: `block-${Date.now()}-${Math.random().toString(36).slice(2, 5)}`,
108      type,
109      messages: [msg],
110      isActive: true,
111      filesChanged: filePath ? [filePath] : [],
112    },
113  ];
114}
