172        if (toolName === "Write" && toolInput?.file_path) {
173          callbacks?.onFileChanged?.(toolInput.file_path as string);
174          const fileName = basename(toolInput.file_path as string);
175          const content =
176            locale === "he"
177              ? `...${fileName} `
178              : `Creating ${fileName}...`;
179          const msg: ChatMessage = {
180            id: `status-${uid()}`,
181            role: "status",
182            content,
183            timestamp: now,
184            toolName,
