207          useAppStore.getState().setCurrentActivity(msg.content);
208          return msg;
209        }
210
211        // Write/Edit without file_path (fallback)
212        if (toolName === "Write" || toolName === "Edit") {
213          const msg: ChatMessage = {
214            id: `status-${uid()}`,
215            role: "status",
216            content:
217              locale === "he" ? "... " : "Making changes...",
218            timestamp: now,
