221          useAppStore.getState().setCurrentActivity(msg.content);
222          return msg;
223        }
224
225        // Glob/Grep/Read  skip (internal, not interesting to user)
226        if (
227          toolName === "Glob" ||
228          toolName === "Grep" ||
229          toolName === "Read"
230        ) {
231          return null;
232        }
233
234        // AskUserQuestion  pass through as question
