 20  "git push": {
 21    en: "Saving your project...",
 22    he: "...  ",
 23  },
 24  "git init": {
 25    en: "Initializing project...",
 26    he: "...  ",
 27  },
 28  "git commit": {
 29    en: "Saving a checkpoint...",
 30    he: "...  ",
 31  },
 32  "npx vercel": {
 33    en: "Publishing to the internet...",
