 3import { useEffect, useRef, useState } from "react";
 4import { useAppStore, type ChatMessage } from "./store";
 5
 6export function useChatPersistence(projectDir: string | null) {
 7  const [isLoaded, setIsLoaded] = useState(false);
 8  const loadMessages = useAppStore((s) => s.loadMessages);
 9  const messages = useAppStore((s) => s.messages);
10  const messagesLoaded = useAppStore((s) => s.messagesLoaded);
11  const saveTimerRef = useRef<ReturnType<typeof setTimeout> | null>(null);
12  const projectDirRef = useRef(projectDir);
13
14  // Track current projectDir for save callback
15  projectDirRef.current = projectDir;
16
17  // Load on mount / project change
18  useEffect(() => {
19    setIsLoaded(false);
20
21    if (!projectDir) {
22      loadMessages([]);
23      setIsLoaded(true);
24      return;
25    }
26
27    const api = window.electronAPI;
28    if (!api?.loadChatHistory) {
29      // Dev mode without Electron  mark as loaded with empty
30      loadMessages([]);
31      setIsLoaded(true);
32      return;
33    }
34
35    api
36      .loadChatHistory(projectDir)
37      .then((msgs) => {
38        loadMessages((msgs || []) as ChatMessage[]);
39        setIsLoaded(true);
40      })
41      .catch(() => {
42        loadMessages([]);
43        setIsLoaded(true);
44      });
45  }, [projectDir, loadMessages]);
46
47  // Save on messages change (debounced)
48  useEffect(() => {
49    if (!messagesLoaded) return;
50    if (!projectDirRef.current) return;
51
52    const api = window.electronAPI;
53    if (!api?.saveChatHistory) return;
54
55    if (saveTimerRef.current) {
56      clearTimeout(saveTimerRef.current);
57    }
58
59    const dir = projectDirRef.current;
60    saveTimerRef.current = setTimeout(() => {
61      api.saveChatHistory!(dir, messages as unknown[]);
62    }, 500);
63
64    return () => {
65      if (saveTimerRef.current) {
66        clearTimeout(saveTimerRef.current);
67      }
68    };
69  }, [messages, messagesLoaded]);
70
71  return { isLoaded };
72}
