/** Translates raw tool calls into friendly status messages */
const toolMessageMap: Record<string, { en: string; he: string }> = {
  "npx create-next-app": {
    en: "Setting up your project...",
    he: "   ...",
  },
  "npm run build": {
    en: "Checking everything works...",
    he: "...  ",
  },
