  1→const { app, BrowserWindow, ipcMain, shell } = require("electron");
  2→const { spawn, execSync } = require("child_process");
  3→const path = require("path");
  4→const fs = require("fs");
  5→const net = require("net");
  6→const log = require("electron-log/main");
  7→const { GitProcess } = require("dugite");
  8→const projectStore = require("./project-store.mjs");
  9→const { slugifyIdea, deduplicatePath } = require("./project-utils");
 10→
 11→// Configure electron-log — write to unified log directory
 12→const LOG_DIR = path.join(require("os").homedir(), ".claude-for-beginners", "logs");
 13→fs.mkdirSync(LOG_DIR, { recursive: true });
 14→
 15→log.initialize();
 16→log.transports.file.resolvePathFn = () => path.join(LOG_DIR, "electron.log");
 17→log.transports.file.maxSize = 5 * 1024 * 1024; // 5 MB per file
 18→log.transports.file.format = "[{y}-{m}-{d} {h}:{i}:{s}.{ms}] [{level}] {text}";
 19→// electron-log auto-rotates: keeps electron.log + electron.old.log
 20→
 21→// Override console so all console.log/error also go to file
 22→Object.assign(console, log.functions);
 23→
 24→log.info("=== Claude for Beginners starting ===");
 25→log.info("Platform:", process.platform, process.arch);
 26→log.info("Electron:", process.versions.electron);
 27→log.info("Node:", process.versions.node);
 28→log.info("App path:", app.getAppPath());
 29→log.info("Log file:", log.transports.file.getFile().path);
 30→
 31→let mainWindow = null;
 32→let nextServer = null;
 33→let serverPort = 3456;
 34→
 35→function getRandomPort() {
 36→  return new Promise((resolve, reject) => {
 37→    const server = net.createServer();
 38→    server.listen(0, "127.0.0.1", () => {
 39→      const addr = server.address();
 40→      if (addr && typeof addr === "object") {
 41→        const port = addr.port;
 42→        server.close(() => resolve(port));
 43→      } else {
 44→        reject(new Error("Failed to get port"));
 45→      }
 46→    });
 47→    server.on("error", reject);
 48→  });
 49→}
 50→
 51→async function startNextServer() {
 52→  const port = await getRandomPort();
 53→  serverPort = port;
 54→  log.info("Starting Next.js standalone server on port", port);
 55→
 56→  const standalonePath = path.join(app.getAppPath(), ".next", "standalone", "server.js");
 57→  nextServer = spawn(process.execPath, [standalonePath], {
 58→    env: {
 59→      ...process.env,
 60→      PORT: String(port),
 61→      HOSTNAME: "127.0.0.1",
 62→    },
 63→    stdio: "pipe",
 64→  });
 65→
 66→  nextServer.stdout?.on("data", (data) => {
 67→    log.info("[next]", data.toString().trim());
 68→  });
 69→
 70→  nextServer.stderr?.on("data", (data) => {
 71→    log.warn("[next]", data.toString().trim());
 72→  });
 73→
 74→  nextServer.on("error", (err) => {
 75→    log.error("[next] Failed to start:", err.message);
 76→  });
 77→
 78→  nextServer.on("close", (code) => {
 79→    log.info("[next] Process exited with code", code);
 80→  });
 81→
 82→  // Wait for server to be ready
 83→  await new Promise((resolve) => {
 84→    const check = () => {
 85→      const req = net.createConnection({ port, host: "127.0.0.1" }, () => {
 86→        req.end();
 87→        resolve();
 88→      });
 89→      req.on("error", () => {
 90→        setTimeout(check, 200);
 91→      });
 92→    };
 93→    check();
 94→  });
 95→
 96→  log.info("Next.js server ready on port", port);
 97→  return port;
 98→}
 99→
100→function createWindow(port) {
101→  const isMac = process.platform === "darwin";
102→  log.info("Creating window, loading http://127.0.0.1:" + port);
103→
104→  mainWindow = new BrowserWindow({
105→    width: 1280,
106→    height: 860,
107→    minWidth: 900,
108→    minHeight: 600,
109→    titleBarStyle: isMac ? "hiddenInset" : "default",
110→    trafficLightPosition: isMac ? { x: 16, y: 16 } : undefined,
111→    backgroundColor: "#FFD700",
112→    webPreferences: {
113→      preload: path.join(__dirname, "preload.js"),
114→      contextIsolation: true,
115→      nodeIntegration: false,
116→      webviewTag: true,
117→    },
118→    show: false,
119→  });
120→
121→  mainWindow.loadURL(`http://127.0.0.1:${port}`);
122→
123→  mainWindow.once("ready-to-show", () => {
124→    log.info("Window ready to show");
125→    mainWindow.show();
126→    if (!app.isPackaged) {
127→      mainWindow.webContents.openDevTools();
128→    }
129→  });
130→
131→  mainWindow.webContents.on("did-fail-load", (_event, errorCode, errorDescription) => {
132→    log.error("Window failed to load:", errorCode, errorDescription);
133→  });
134→
135→  mainWindow.webContents.on("did-finish-load", () => {
136→    log.info("Window finished loading");
137→  });
138→
139→  // Open external links in default browser
140→  mainWindow.webContents.setWindowOpenHandler(({ url }) => {
141→    log.info("Opening external URL:", url);
142→    shell.openExternal(url);
143→    return { action: "deny" };
144→  });
145→
146→  mainWindow.on("closed", () => {
147→    log.info("Window closed");
148→    mainWindow = null;
149→  });
150→}
151→
152→// --- Claude binary discovery ---
153→
154→function findClaude() {
155→  const home = require("os").homedir();
156→  const candidates = [
157→    path.join(home, ".local", "bin", "claude"),
158→    path.join(home, ".claude", "bin", "claude"),
159→    "/usr/local/bin/claude",
160→    "/opt/homebrew/bin/claude",
161→  ];
162→  for (const p of candidates) {
163→    if (fs.existsSync(p)) return p;
164→  }
165→  return null;
166→}
167→
168→// --- Skill file helpers ---
169→
170→function getSkillSourceDir() {
171→  if (app.isPackaged) {
172→    return path.join(process.resourcesPath, "cc4d-skill");
173→  }
174→  // Dev mode — resolve relative to this file: electron/main.js → ../../skills/cc4d
175→  return path.join(__dirname, "..", "..", "skills", "cc4d");
176→}
177→
178→function copyDirRecursive(src, dest) {
179→  fs.mkdirSync(dest, { recursive: true });
180→  for (const entry of fs.readdirSync(src, { withFileTypes: true })) {
181→    const srcPath = path.join(src, entry.name);
182→    const destPath = path.join(dest, entry.name);
183→    if (entry.isDirectory()) {
184→      copyDirRecursive(srcPath, destPath);
185→    } else {
186→      fs.copyFileSync(srcPath, destPath);
187→    }
188→  }
189→}
190→
191→function copySkillFiles(projectPath) {
192→  const src = getSkillSourceDir();
193→  const dest = path.join(projectPath, ".cc4d");
194→  copyDirRecursive(src, dest);
195→  // Ensure progress.sh is executable
196→  const progressSh = path.join(dest, "scripts", "progress.sh");
197→  if (fs.existsSync(progressSh)) {
198→    fs.chmodSync(progressSh, 0o755);
199→  }
200→  log.info("[project] Copied skill files to", dest);
201→}
202→
203→function writeProjectClaudeMd(projectPath, preferences) {
204→  const skillMdPath = path.join(getSkillSourceDir(), "SKILL.md");
205→  let content = fs.readFileSync(skillMdPath, "utf-8");
206→
207→  // Strip YAML frontmatter
208→  content = content.replace(/^---[\s\S]*?---\s*\n/, "");
209→
210→  // Adjust paths: scripts/ → .cc4d/scripts/, references/ → .cc4d/references/, steps/ → .cc4d/steps/
211→  content = content.replace(/\bscripts\//g, ".cc4d/scripts/");
212→  content = content.replace(/\breferences\//g, ".cc4d/references/");
213→  content = content.replace(/\bsteps\//g, ".cc4d/steps/");
214→
215→  // Add note about Steps 1-3 being pre-completed by desktop onboarding
216→  content += "\n\n## Desktop App Note\n\nSteps 1-3 (environment check, orient user, gather idea/preferences) have been pre-completed by the desktop app. The progress state starts at Step 4 (scaffold & build). The user's idea and preferences are listed below.\n";
217→
218→  // Append user preferences if provided
219→  if (preferences) {
220→    content += "\n## What we know (from desktop app onboarding)\n";
221→    if (preferences.idea) content += `- **Idea**: ${preferences.idea}\n`;
222→    if (preferences.vibe) content += `- **Style/Vibe**: ${preferences.vibe}\n`;
223→    if (preferences.audience) content += `- **Audience**: ${preferences.audience}\n`;
224→    if (preferences.priority) content += `- **Priority**: ${preferences.priority}\n`;
225→    content += `- **Design reference**: ${preferences.designRef || "Surprise me"}\n`;
226→  }
227→
228→  const claudeMdPath = path.join(projectPath, "CLAUDE.md");
229→  fs.writeFileSync(claudeMdPath, content, "utf-8");
230→  log.info("[project] Wrote CLAUDE.md to", claudeMdPath);
231→}
232→
233→function initProgressState(projectPath) {
234→  const state = {
235→    current_step: 4,
236→    completed: [1, 2, 3],
237→    phase: 1,
238→  };
239→  const statePath = path.join(projectPath, ".cc4d-progress.json");
240→  fs.writeFileSync(statePath, JSON.stringify(state, null, 2), "utf-8");
241→  log.info("[project] Initialized progress state at step 4");
242→}
243→
244→// --- Project directory helpers ---
245→
246→function getProjectsDir() {
247→  const home = app.getPath("home");
248→  return path.join(home, "Documents", "Claude Projects");
249→}
250→
251→function setupIPC() {
252→  ipcMain.handle("locale:get", () => {
253→    const osLocale = app.getLocale();
254→    const locale = osLocale.startsWith("he") ? "he" : "en";
255→    log.info("Locale requested:", osLocale, "→", locale);
256→    return locale;
257→  });
258→
259→  ipcMain.handle("runtime:status", async () => {
260→    let nodeReady = false;
261→    try {
262→      const nodeVersion = execSync("node --version", { encoding: "utf-8", timeout: 5000 }).trim();
263→      const major = parseInt(nodeVersion.replace("v", "").split(".")[0], 10);
264→      nodeReady = major >= 18;
265→    } catch {}
266→
267→    let gitReady = false;
268→    try {
269→      execSync("git --version", { timeout: 5000 });
270→      gitReady = true;
271→    } catch {}
272→
273→    const claudeReady = findClaude() !== null;
274→
275→    const status = { nodeReady, gitReady, claudeReady };
276→    log.debug("Runtime status:", status);
277→    return status;
278→  });
279→
280→  ipcMain.handle("project:open", (_event, projectPath) => {
281→    log.info("Opening project path:", projectPath);
282→    shell.openPath(projectPath);
283→  });
284→
285→  ipcMain.handle("shell:open-external", (_event, url) => {
286→    log.info("Opening external:", url);
287→    shell.openExternal(url);
288→  });
289→
290→  ipcMain.handle("log:get-path", () => {
291→    return log.transports.file.getFile().path;
292→  });
293→
294→  // --- Renderer log forwarding ---
295→  const rendererLogPath = path.join(LOG_DIR, "renderer.log");
296→  const RENDERER_MAX_SIZE = 5 * 1024 * 1024; // 5 MB
297→
298→  ipcMain.on("log:from-renderer", (_event, { level, message }) => {
299→    const line = `[${new Date().toISOString()}] [${level.toUpperCase()}] ${message}\n`;
300→    try {
301→      // Simple size-based rotation
302→      try {
303→        const stat = fs.statSync(rendererLogPath);
304→        if (stat.size >= RENDERER_MAX_SIZE) {
305→          const oldPath = rendererLogPath + ".old";
306→          try { fs.unlinkSync(oldPath); } catch {}
307→          fs.renameSync(rendererLogPath, oldPath);
308→        }
309→      } catch {}
310→      fs.appendFileSync(rendererLogPath, line);
311→    } catch {}
312→  });
313→
314→  // --- Project management ---
315→
316→  ipcMain.handle("project:create", async (_event, { idea, locale }) => {
317→    try {
318→      const baseDir = getProjectsDir();
319→      fs.mkdirSync(baseDir, { recursive: true });
320→
321→      const hasIdea = idea && idea.trim();
322→      const slug = hasIdea ? slugifyIdea(idea) : "new-project";
323→      const name = await deduplicatePath(baseDir, slug);
324→      const projectPath = path.join(baseDir, name);
325→
326→      fs.mkdirSync(projectPath, { recursive: true });
327→      log.info("[project] Created directory:", projectPath);
328→
329→      // git init
330→      const gitResult = await GitProcess.exec(["init"], projectPath);
331→      if (gitResult.exitCode !== 0) {
332→        log.warn("[project] git init warning:", gitResult.stderr);
333→      } else {
334→        log.info("[project] git init OK in", projectPath);
335→      }
336→
337→      // Copy skill files, write CLAUDE.md, initialize progress
338→      copySkillFiles(projectPath);
339→      writeProjectClaudeMd(projectPath, hasIdea ? { idea: idea.trim() } : null);
340→      initProgressState(projectPath);
341→
342→      const now = new Date().toISOString();
343→      const meta = {
344→        id: `proj-${Date.now()}-${Math.random().toString(36).slice(2, 6)}`,
345→        name,
346→        displayName: hasIdea ? idea.trim().slice(0, 80) : "",
347→        path: projectPath,
348→        createdAt: now,
349→        lastOpenedAt: now,
350→        sessionId: null,
351→        locale: locale || "he",
352→        vibe: null,
353→        audience: null,
354→        priority: null,
355→        designRef: null,
356→        liveUrl: null,
357→        githubUrl: null,
358→      };
359→
360→      projectStore.addProject(meta);
361→      log.info("[project] Created", name, "at", projectPath);
362→      return meta;
363→    } catch (err) {
364→      log.error("[project] Create failed:", err.message);
365→      throw err;
366→    }
367→  });
368→
369→  ipcMain.handle("project:list", () => {
370→    const projects = projectStore.getProjects();
371→    return projects.sort(
372→      (a, b) => new Date(b.lastOpenedAt).getTime() - new Date(a.lastOpenedAt).getTime()
373→    );
374→  });
375→
376→  ipcMain.handle("project:switch", (_event, id) => {
377→    const project = projectStore.getProject(id);
378→    if (!project) {
379→      log.warn("[project] Switch failed — not found:", id);
380→      return null;
381→    }
