const { app, BrowserWindow, ipcMain, shell } = require("electron");
const { spawn, execSync } = require("child_process");
const path = require("path");
const fs = require("fs");
const net = require("net");
const log = require("electron-log/main");
const { GitProcess } = require("dugite");
const projectStore = require("./project-store.mjs");
const { slugifyIdea, deduplicatePath } = require("./project-utils");

// Configure electron-log — write to unified log directory
const LOG_DIR = path.join(require("os").homedir(), ".claude-for-beginners", "logs");
fs.mkdirSync(LOG_DIR, { recursive: true });

log.initialize();
log.transports.file.resolvePathFn = () => path.join(LOG_DIR, "electron.log");
log.transports.file.maxSize = 5 * 1024 * 1024; // 5 MB per file
log.transports.file.format = "[{y}-{m}-{d} {h}:{i}:{s}.{ms}] [{level}] {text}";
// electron-log auto-rotates: keeps electron.log + electron.old.log

// Override console so all console.log/error also go to file
Object.assign(console, log.functions);

log.info("=== Claude for Beginners starting ===");
log.info("Platform:", process.platform, process.arch);
log.info("Electron:", process.versions.electron);
log.info("Node:", process.versions.node);
log.info("App path:", app.getAppPath());
log.info("Log file:", log.transports.file.getFile().path);

let mainWindow = null;
let nextServer = null;
let serverPort = 3456;

function getRandomPort() {
  return new Promise((resolve, reject) => {
    const server = net.createServer();
    server.listen(0, "127.0.0.1", () => {
      const addr = server.address();
      if (addr && typeof addr === "object") {
        const port = addr.port;
        server.close(() => resolve(port));
      } else {
        reject(new Error("Failed to get port"));
      }
    });
    server.on("error", reject);
  });
}

async function startNextServer() {
  const port = await getRandomPort();
  serverPort = port;
  log.info("Starting Next.js standalone server on port", port);

  const standalonePath = path.join(app.getAppPath(), ".next", "standalone", "server.js");
  nextServer = spawn(process.execPath, [standalonePath], {
    env: {
      ...process.env,
      PORT: String(port),
      HOSTNAME: "127.0.0.1",
    },
    stdio: "pipe",
  });

  nextServer.stdout?.on("data", (data) => {
    log.info("[next]", data.toString().trim());
  });

  nextServer.stderr?.on("data", (data) => {
    log.warn("[next]", data.toString().trim());
  });

  nextServer.on("error", (err) => {
    log.error("[next] Failed to start:", err.message);
  });

  nextServer.on("close", (code) => {
    log.info("[next] Process exited with code", code);
  });

  // Wait for server to be ready
  await new Promise((resolve) => {
    const check = () => {
      const req = net.createConnection({ port, host: "127.0.0.1" }, () => {
        req.end();
        resolve();
      });
      req.on("error", () => {
        setTimeout(check, 200);
      });
    };
    check();
  });

  log.info("Next.js server ready on port", port);
  return port;
}

function createWindow(port) {
  const isMac = process.platform === "darwin";
  log.info("Creating window, loading http://127.0.0.1:" + port);

  mainWindow = new BrowserWindow({
    width: 1280,
    height: 860,
    minWidth: 900,
    minHeight: 600,
    titleBarStyle: isMac ? "hiddenInset" : "default",
    trafficLightPosition: isMac ? { x: 16, y: 16 } : undefined,
    backgroundColor: "#FFD700",
    webPreferences: {
      preload: path.join(__dirname, "preload.js"),
      contextIsolation: true,
      nodeIntegration: false,
      webviewTag: true,
    },
    show: false,
  });

  mainWindow.loadURL(`http://127.0.0.1:${port}`);

  mainWindow.once("ready-to-show", () => {
    log.info("Window ready to show");
    mainWindow.show();
    if (!app.isPackaged) {
      mainWindow.webContents.openDevTools();
    }
  });

  mainWindow.webContents.on("did-fail-load", (_event, errorCode, errorDescription) => {
    log.error("Window failed to load:", errorCode, errorDescription);
  });

  mainWindow.webContents.on("did-finish-load", () => {
    log.info("Window finished loading");
  });

  // Open external links in default browser
  mainWindow.webContents.setWindowOpenHandler(({ url }) => {
    log.info("Opening external URL:", url);
    shell.openExternal(url);
    return { action: "deny" };
  });

  mainWindow.on("closed", () => {
    log.info("Window closed");
    mainWindow = null;
  });
}

// --- Claude binary discovery ---

function findClaude() {
  const home = require("os").homedir();
  const candidates = [
    path.join(home, ".local", "bin", "claude"),
    path.join(home, ".claude", "bin", "claude"),
    "/usr/local/bin/claude",
    "/opt/homebrew/bin/claude",
  ];
  for (const p of candidates) {
    if (fs.existsSync(p)) return p;
  }
  return null;
}

// --- Skill file helpers ---

function getSkillSourceDir() {
  if (app.isPackaged) {
    return path.join(process.resourcesPath, "cc4d-skill");
  }
  // Dev mode — resolve relative to this file: electron/main.js → ../../skills/cc4d
  return path.join(__dirname, "..", "..", "skills", "cc4d");
}

function copyDirRecursive(src, dest) {
  fs.mkdirSync(dest, { recursive: true });
  for (const entry of fs.readdirSync(src, { withFileTypes: true })) {
    const srcPath = path.join(src, entry.name);
    const destPath = path.join(dest, entry.name);
    if (entry.isDirectory()) {
      copyDirRecursive(srcPath, destPath);
    } else {
      fs.copyFileSync(srcPath, destPath);
    }
  }
}

function copySkillFiles(projectPath) {
  const src = getSkillSourceDir();
  const dest = path.join(projectPath, ".cc4d");
  copyDirRecursive(src, dest);
  // Ensure progress.sh is executable
  const progressSh = path.join(dest, "scripts", "progress.sh");
  if (fs.existsSync(progressSh)) {
    fs.chmodSync(progressSh, 0o755);
  }
  log.info("[project] Copied skill files to", dest);
}

function writeProjectClaudeMd(projectPath, preferences) {
  const skillMdPath = path.join(getSkillSourceDir(), "SKILL.md");
  let content = fs.readFileSync(skillMdPath, "utf-8");

  // Strip YAML frontmatter
  content = content.replace(/^---[\s\S]*?---\s*\n/, "");

  // Adjust paths: scripts/ → .cc4d/scripts/, references/ → .cc4d/references/, steps/ → .cc4d/steps/
  content = content.replace(/\bscripts\//g, ".cc4d/scripts/");
  content = content.replace(/\breferences\//g, ".cc4d/references/");
  content = content.replace(/\bsteps\//g, ".cc4d/steps/");

  // Add note about Steps 1-3 being pre-completed by desktop onboarding
  content += "\n\n## Desktop App Note\n\nSteps 1-3 (environment check, orient user, gather idea/preferences) have been pre-completed by the desktop app. The progress state starts at Step 4 (scaffold & build). The user's idea and preferences are listed below.\n";

  // Append user preferences if provided
  if (preferences) {
    content += "\n## What we know (from desktop app onboarding)\n";
    if (preferences.idea) content += `- **Idea**: ${preferences.idea}\n`;
    if (preferences.vibe) content += `- **Style/Vibe**: ${preferences.vibe}\n`;
    if (preferences.audience) content += `- **Audience**: ${preferences.audience}\n`;
    if (preferences.priority) content += `- **Priority**: ${preferences.priority}\n`;
    content += `- **Design reference**: ${preferences.designRef || "Surprise me"}\n`;
  }

  const claudeMdPath = path.join(projectPath, "CLAUDE.md");
  fs.writeFileSync(claudeMdPath, content, "utf-8");
  log.info("[project] Wrote CLAUDE.md to", claudeMdPath);
}

function initProgressState(projectPath) {
  const state = {
    current_step: 4,
    completed: [1, 2, 3],
    phase: 1,
  };
  const statePath = path.join(projectPath, ".cc4d-progress.json");
  fs.writeFileSync(statePath, JSON.stringify(state, null, 2), "utf-8");
  log.info("[project] Initialized progress state at step 4");
}

// --- Project directory helpers ---

function getProjectsDir() {
  const home = app.getPath("home");
  return path.join(home, "Documents", "Claude Projects");
}

function setupIPC() {
  ipcMain.handle("locale:get", () => {
    const osLocale = app.getLocale();
    const locale = osLocale.startsWith("he") ? "he" : "en";
    log.info("Locale requested:", osLocale, "→", locale);
    return locale;
  });

  ipcMain.handle("runtime:status", async () => {
    let nodeReady = false;
    try {
      const nodeVersion = execSync("node --version", { encoding: "utf-8", timeout: 5000 }).trim();
      const major = parseInt(nodeVersion.replace("v", "").split(".")[0], 10);
      nodeReady = major >= 18;
    } catch {}

    let gitReady = false;
    try {
      execSync("git --version", { timeout: 5000 });
      gitReady = true;
    } catch {}

    const claudeReady = findClaude() !== null;

    const status = { nodeReady, gitReady, claudeReady };
    log.debug("Runtime status:", status);
    return status;
  });

  ipcMain.handle("project:open", (_event, projectPath) => {
    log.info("Opening project path:", projectPath);
    shell.openPath(projectPath);
  });

  ipcMain.handle("shell:open-external", (_event, url) => {
    log.info("Opening external:", url);
    shell.openExternal(url);
  });

  ipcMain.handle("log:get-path", () => {
    return log.transports.file.getFile().path;
  });

  // --- Renderer log forwarding ---
  const rendererLogPath = path.join(LOG_DIR, "renderer.log");
  const RENDERER_MAX_SIZE = 5 * 1024 * 1024; // 5 MB

  ipcMain.on("log:from-renderer", (_event, { level, message }) => {
    const line = `[${new Date().toISOString()}] [${level.toUpperCase()}] ${message}\n`;
    try {
      // Simple size-based rotation
      try {
        const stat = fs.statSync(rendererLogPath);
        if (stat.size >= RENDERER_MAX_SIZE) {
          const oldPath = rendererLogPath + ".old";
          try { fs.unlinkSync(oldPath); } catch {}
          fs.renameSync(rendererLogPath, oldPath);
        }
      } catch {}
      fs.appendFileSync(rendererLogPath, line);
    } catch {}
  });

  // --- Project management ---

  ipcMain.handle("project:create", async (_event, { idea, locale }) => {
    try {
      const baseDir = getProjectsDir();
      fs.mkdirSync(baseDir, { recursive: true });

      const hasIdea = idea && idea.trim();
      const slug = hasIdea ? slugifyIdea(idea) : "new-project";
      const name = await deduplicatePath(baseDir, slug);
      const projectPath = path.join(baseDir, name);

      fs.mkdirSync(projectPath, { recursive: true });
      log.info("[project] Created directory:", projectPath);

      // git init
      const gitResult = await GitProcess.exec(["init"], projectPath);
      if (gitResult.exitCode !== 0) {
        log.warn("[project] git init warning:", gitResult.stderr);
      } else {
        log.info("[project] git init OK in", projectPath);
      }

      // Copy skill files, write CLAUDE.md, initialize progress
      copySkillFiles(projectPath);
      writeProjectClaudeMd(projectPath, hasIdea ? { idea: idea.trim() } : null);
      initProgressState(projectPath);

      const now = new Date().toISOString();
      const meta = {
        id: `proj-${Date.now()}-${Math.random().toString(36).slice(2, 6)}`,
        name,
        displayName: hasIdea ? idea.trim().slice(0, 80) : "",
        path: projectPath,
        createdAt: now,
        lastOpenedAt: now,
        sessionId: null,
        locale: locale || "he",
        vibe: null,
        audience: null,
        priority: null,
        designRef: null,
        liveUrl: null,
        githubUrl: null,
      };

      projectStore.addProject(meta);
      log.info("[project] Created", name, "at", projectPath);
      return meta;
    } catch (err) {
      log.error("[project] Create failed:", err.message);
      throw err;
    }
  });

  ipcMain.handle("project:list", () => {
    const projects = projectStore.getProjects();
    return projects.sort(
      (a, b) => new Date(b.lastOpenedAt).getTime() - new Date(a.lastOpenedAt).getTime()
    );
  });

  ipcMain.handle("project:switch", (_event, id) => {
    const project = projectStore.getProject(id);
    if (!project) {
      log.warn("[project] Switch failed — not found:", id);
      return null;
    }
    if (!fs.existsSync(project.path)) {
      log.warn("[project] Switch failed — dir missing:", project.path);
      return null;
    }
    projectStore.updateProject(id, { lastOpenedAt: new Date().toISOString() });
    projectStore.setActiveProjectId(id);
    log.info("[project] Switched to", project.name);
    return projectStore.getProject(id);
  });

  ipcMain.handle("project:update", (_event, { id, updates }) => {
    const updated = projectStore.updateProject(id, updates);
    if (updated) {
      log.info("[project] Updated", id, Object.keys(updates).join(", "));
    }
    return updated;
  });

  ipcMain.handle("project:get-active", () => {
    return projectStore.getActiveProject();
  });

  // --- Chat history persistence ---

  ipcMain.handle("chat:load", async (_event, projectPath) => {
    try {
      const filePath = path.join(projectPath, ".cc4d", "chat-history.json");
      const data = fs.readFileSync(filePath, "utf-8");
      return JSON.parse(data);
    } catch {
      return [];
    }
  });

  ipcMain.handle("chat:save", async (_event, { projectPath, messages }) => {
    try {
      const dir = path.join(projectPath, ".cc4d");
      fs.mkdirSync(dir, { recursive: true });
      const filePath = path.join(dir, "chat-history.json");
      const tmpPath = filePath + ".tmp";
      fs.writeFileSync(tmpPath, JSON.stringify(messages), "utf-8");
      fs.renameSync(tmpPath, filePath);
      return true;
    } catch (err) {
      log.error("[chat] Save failed:", err.message);
      return false;
    }
  });

  // --- Finalize preferences (write to CLAUDE.md after welcome flow) ---

  ipcMain.handle("project:finalize-preferences", async (_event, { projectId, preferences }) => {
    try {
      const project = projectStore.getProject(projectId);
      if (!project) {
        log.warn("[project] finalize-preferences: project not found", projectId);
        return false;
      }

      const projectPath = project.path;

      // Re-write CLAUDE.md with full preferences
      writeProjectClaudeMd(projectPath, preferences);

      // Update progress to step 4
      const state = {
        current_step: 4,
        completed: [1, 2, 3],
        phase: 1,
      };
      const statePath = path.join(projectPath, ".cc4d-progress.json");
      fs.writeFileSync(statePath, JSON.stringify(state, null, 2), "utf-8");

      // Persist preferences to electron-store
      projectStore.updateProject(projectId, {
        vibe: preferences.vibe || null,
        audience: preferences.audience || null,
        priority: preferences.priority || null,
        designRef: preferences.designRef || null,
      });

      log.info("[project] Finalized preferences for", project.name);
      return true;
    } catch (err) {
      log.error("[project] finalize-preferences failed:", err.message);
      return false;
    }
  });

  // --- Milestones persistence ---

  ipcMain.handle("milestones:load", async (_event, projectPath) => {
    try {
      const filePath = path.join(projectPath, ".cc4d", "milestones.json");
      const data = fs.readFileSync(filePath, "utf-8");
      return JSON.parse(data);
    } catch {
      return [];
    }
  });

  ipcMain.handle("milestones:save", async (_event, { projectPath, milestones }) => {
    try {
      const dir = path.join(projectPath, ".cc4d");
      fs.mkdirSync(dir, { recursive: true });
      const filePath = path.join(dir, "milestones.json");
      const tmpPath = filePath + ".tmp";
      fs.writeFileSync(tmpPath, JSON.stringify(milestones), "utf-8");
      fs.renameSync(tmpPath, filePath);
      return true;
    } catch (err) {
      log.error("[milestones] Save failed:", err.message);
      return false;
    }
  });

  // --- Preview port polling ---

  ipcMain.handle("preview:poll-port", async (_event, port) => {
    const timeout = 30000;
    const start = Date.now();
    while (Date.now() - start < timeout) {
      try {
        await new Promise((resolve, reject) => {
          const conn = net.createConnection({ port, host: "127.0.0.1" }, () => {
            conn.end();
            resolve(true);
          });
          conn.on("error", reject);
          conn.setTimeout(1000, () => {
            conn.destroy();
            reject(new Error("timeout"));
          });
        });
        return true;
      } catch {
        await new Promise((r) => setTimeout(r, 500));
      }
    }
    return false;
  });

  log.info("IPC handlers registered");
}

app.whenReady().then(async () => {
  setupIPC();

  const isDev = !app.isPackaged;
  log.info("Dev mode:", isDev);

  // In dev, Next.js runs externally via concurrently on port 3456
  const port = isDev ? 3456 : await startNextServer();

  createWindow(port);

  app.on("activate", () => {
    if (BrowserWindow.getAllWindows().length === 0) {
      log.info("Reactivating — creating new window");
      createWindow(port);
    }
  });
});

app.on("window-all-closed", () => {
  log.info("All windows closed");
  if (process.platform !== "darwin") {
    app.quit();
  }
});

app.on("before-quit", () => {
  log.info("=== App quitting ===");
  if (nextServer) {
    log.info("Killing Next.js server");
    nextServer.kill();
    nextServer = null;
  }
});

