const fs = require('fs');
const { execSync } = require('child_process');
const path = require('path');

// Create ICO from multiple PNG sizes
// ICO format: header + directory entries + image data
const sizes = [16, 32, 48, 256];
const pngBuffers = [];

for (const size of sizes) {
  const tmpFile = '/tmp/icon_' + size + '.png';
  execSync('sips -z ' + size + ' ' + size + ' public/mascot/hero.png --out ' + tmpFile);
  pngBuffers.push({ size, data: fs.readFileSync(tmpFile) });
}

// ICO header: 6 bytes
const header = Buffer.alloc(6);
header.writeUInt16LE(0, 0);     // reserved
header.writeUInt16LE(1, 2);     // type: 1 = ICO
header.writeUInt16LE(pngBuffers.length, 4); // count

// Directory entries: 16 bytes each
const dirSize = 16 * pngBuffers.length;
let dataOffset = 6 + dirSize;

const dirEntries = [];
for (const { size, data } of pngBuffers) {
  const entry = Buffer.alloc(16);
  entry.writeUInt8(size < 256 ? size : 0, 0);  // width (0 = 256)
  entry.writeUInt8(size < 256 ? size : 0, 1);  // height
  entry.writeUInt8(0, 2);     // color palette
  entry.writeUInt8(0, 3);     // reserved
  entry.writeUInt16LE(1, 4);  // color planes
  entry.writeUInt16LE(32, 6); // bits per pixel
  entry.writeUInt32LE(data.length, 8);  // image size
  entry.writeUInt32LE(dataOffset, 12);  // offset
  dirEntries.push(entry);
  dataOffset += data.length;
}

const ico = Buffer.concat([header, ...dirEntries, ...pngBuffers.map(p => p.data)]);
fs.writeFileSync('public/icon.ico', ico);
console.log('ICO created:', ico.length, 'bytes');
